import React, { useState, useEffect } from 'react';
import { Dropdown } from 'react-bootstrap';

const VendorStatusDropdown = ({ vendor }) => {
  const [status, setStatus] = useState('Closed');

  // Helper function to check if the current time is within business hours
  const checkStatus = () => {
    const today = new Date();
    const currentDay = today.toLocaleString('en-US', { weekday: 'long' }).toLowerCase();
    const currentTime = today.toTimeString().slice(0, 5); // Current time as "HH:MM"

    const { open, close } = vendor.businessHours[currentDay] || {};
    if (open && close) {
      setStatus(currentTime >= open && currentTime < close ? 'Available' : 'Closed');
    } else {
      setStatus('Closed');
    }
  };

  useEffect(() => {
    checkStatus();
    const interval = setInterval(checkStatus, 60000); // Check status every minute
    return () => clearInterval(interval);
  }, [vendor.businessHours]);

  return (
    <Dropdown>
      <Dropdown.Toggle
        variant={status === 'Available' ? 'success' : 'danger'}
        id="vendor-status-dropdown"
        className="mb-3 btn-sm"
      >
        {status}
      </Dropdown.Toggle>

      <Dropdown.Menu className="vendor-status-dropdown-menu">
        <Dropdown.Item disabled={status === 'Closed'} href={`tel:${vendor.contactNumber}`}>
          Call Vendor
        </Dropdown.Item>
        <Dropdown.Item disabled={status === 'Closed'} href={`sms:${vendor.contactNumber}`}>
          Text Vendor
        </Dropdown.Item>
        <Dropdown.Divider />
        <div className="px-3">
          <strong>${vendor.minOrder || 'N/A'} Minimum</strong>
        </div>
        <div className="px-3 mt-2">
          <strong>Delivery Hours:</strong>
          <ul className="vendor-business-hours-list list-unstyled mb-0">
            {Object.entries(vendor.businessHours).map(([day, hours]) => (
              <li key={day}>
                <span>{day.charAt(0).toUpperCase() + day.slice(1)}</span>
                <span>{hours.open && hours.close ? `${hours.open} - ${hours.close}` : 'Closed'}</span>
              </li>
            ))}
          </ul>
        </div>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default VendorStatusDropdown;
