import React, { useState } from 'react';

const MinOrderComponent = ({ minOrder, onUpdate }) => {
  const [newMinOrder, setNewMinOrder] = useState(minOrder);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newMinOrder < 0) {
      alert('Minimum order cannot be less than 0');
      return;
    }
    onUpdate(newMinOrder);
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded">
      <label htmlFor="minOrder" className="form-label">
        Minimum Order Amount:
      </label>
      <input
        type="number"
        id="minOrder"
        className="form-control"
        value={newMinOrder}
        onChange={(e) => setNewMinOrder(e.target.value)}
        min="0"
      />
      <button type="submit" className="btn btn-primary mt-3">
        Update Minimum Order
      </button>
    </form>
  );
};

export default MinOrderComponent;
