import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import { BsPersonCircle } from 'react-icons/bs';

const VendorProfileCard = () => {
  const [vendor, setVendor] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [alert, setAlert] = useState({ message: '', variant: '' });

  // Form Data for editing vendor info
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    contactNumber: '',
    email: '',
  });

  // Logo management states
  const [logoUrl, setLogoUrl] = useState('');
  const [previewUrl, setPreviewUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [file, setFile] = useState(null);

  // Fetch vendor details on mount
  useEffect(() => {
    const fetchVendorData = async () => {
      try {
        const response = await axios.get('/api/vendors/profile', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        const vendorData = response.data.data;
        setVendor(vendorData);
        setLogoUrl(vendorData.logoUrl || '');
      } catch (error) {
        console.error('Error fetching vendor details:', error);
        setAlert({ message: 'Failed to load vendor details.', variant: 'danger' });
      }
    };
    fetchVendorData();
  }, []);

  // Handle File Selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreviewUrl(reader.result);
      reader.readAsDataURL(selectedFile);
    }
  };

  // Handle Logo Upload
  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file to upload.');
      return;
    }
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('logo', file);

      const response = await axios.post('/api/vendors/upload-logo', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setLogoUrl(response.data.logoUrl);
      setFile(null);
      setPreviewUrl('');
      alert('Logo updated successfully!');
    } catch (error) {
      console.error('Error uploading logo:', error);
      alert('Failed to upload logo.');
    } finally {
      setLoading(false);
    }
  };

  // Handle Edit Button Click
  const handleEdit = () => {
    setFormData({
      firstName: vendor.firstName || '',
      lastName: vendor.lastName || '',
      contactNumber: formatPhoneNumber(vendor.contactNumber || ''),
      email: vendor.email || '',
    });
    setShowModal(true);
  };

  // Format phone number
  const formatPhoneNumber = (value) => {
    const cleaned = value.replace(/\D/g, '');
    const match = cleaned.match(/^(\d{0,3})(\d{0,3})(\d{0,4})$/);
    if (!match) return value;
    return [match[1] && `(${match[1]}`, match[2] && `) ${match[2]}`, match[3] && `-${match[3]}`]
      .filter(Boolean)
      .join('');
  };

  // Handle Update
  const handleUpdate = async () => {
    const cleanedPhoneNumber = formData.contactNumber.replace(/\D/g, '');
    if (cleanedPhoneNumber.length !== 10) {
      setAlert({ message: 'Phone number must be 10 digits.', variant: 'danger' });
      return;
    }

    try {
      const response = await axios.put(
        '/api/vendors/profile',
        { ...formData, contactNumber: cleanedPhoneNumber },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setVendor(response.data.data);
      setShowModal(false);
      setAlert({ message: 'Details updated successfully!', variant: 'success' });
    } catch (error) {
      console.error('Error updating details:', error);
      setAlert({ message: 'Failed to update details.', variant: 'danger' });
    }
  };

  return (
    <Card className="shadow-sm p-3">
      <Row className="align-items-center">
        {/* Logo Section */}
        <Col xs={12} md={4} className="text-center">
          {previewUrl ? (
            <img
              src={previewUrl}
              alt="Preview"
              className="rounded-circle border border-secondary"
              style={{ width: '150px', height: '150px', objectFit: 'cover' }}
            />
          ) : logoUrl ? (
            <img
              src={logoUrl}
              alt="Vendor Logo"
              className="rounded-circle"
              style={{ width: '150px', height: '150px', objectFit: 'cover' }}
            />
          ) : (
            <BsPersonCircle size={150} className="text-secondary" />
          )}
          <div className="mt-2">
            <Button
              variant="link"
              className="p-0"
              onClick={() => document.getElementById('fileInput').click()}
            >
              {logoUrl || previewUrl ? 'Edit Logo' : 'Upload Logo'}
            </Button>
            <input
              type="file"
              id="fileInput"
              style={{ display: 'none' }}
              accept="image/*"
              onChange={handleFileChange}
            />
            {file && (
              <Button
                variant="primary"
                size="sm"
                className="mt-2"
                disabled={loading}
                onClick={handleUpload}
              >
                {loading ? 'Uploading...' : 'Save Logo'}
              </Button>
            )}
          </div>
        </Col>

        {/* Info Section */}
        <Col xs={12} md={8}>
          {alert.message && <Alert variant={alert.variant}>{alert.message}</Alert>}
          <h5>
            {vendor.firstName || 'N/A'} {vendor.lastName || ''}
          </h5>
          <p className="mb-1">
            <strong>Contact:</strong> {formatPhoneNumber(vendor.contactNumber || 'N/A')}
          </p>
          <p>
            <strong>Email:</strong> {vendor.email || 'N/A'}
          </p>
          <Button variant="secondary" size="sm" onClick={handleEdit}>
            Edit Details
          </Button>
        </Col>
      </Row>

      {/* Modal for Editing Info */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Edit Vendor Information</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Contact Number</Form.Label>
              <Form.Control
                type="text"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={(e) => setFormData({ ...formData, contactNumber: e.target.value })}
                maxLength="14"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleUpdate}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>
    </Card>
  );
};

export default VendorProfileCard;
