import React, { useState, useEffect } from 'react';
import NearbyVendorsPage from './NearbyVendorsPage';
import NearbyPickupPage from './NearbyPickupPage';
import SearchBar from './SearchBar';
import DeliveryPickupToggle from './DeliveryPickupToggle';
import './DeliveryPickupWrapper.css'; // Add custom styles if needed

const DeliveryPickupWrapper = () => {
  const [selectedOption, setSelectedOption] = useState('delivery');
  const [searchQuery, setSearchQuery] = useState('');
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true); // Trigger animation after mounting
  }, []);

  return (
    <div
      className={`delivery-pickup-wrapper container-fluid d-flex justify-content-center align-items-center p-0`}
      style={{
        minHeight: '100vh',
        backgroundColor: '#F0F0F0', // Set the background color outside the card
      }}
    >
      <div
        className={`delivery-pickup-card shadow-sm ${animate ? 'slide-in' : ''}`}
        style={{
          backgroundColor: '#FFFFFF',
          borderRadius: '16px',
          width: '90%',
          maxWidth: '1200px',
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
          overflow: 'hidden',
        }}
      >
        {/* Toolbar Section */}
        <div
          className="toolbar-container shadow-sm py-3 px-3"
          style={{
            position: 'sticky',
            top: 0,
            zIndex: 1030,
            backgroundColor: '#F7F7F7',
            borderBottom: '1px solid #ddd',
          }}
        >
          <div className="d-flex align-items-center justify-content-between">
            {/* Reusable SearchBar Component */}
            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
            <DeliveryPickupToggle selectedOption={selectedOption} setSelectedOption={setSelectedOption} />
          </div>
        </div>

        {/* Conditional Rendering of Pages */}
        <div className="content-section p-3" style={{ minHeight: '400px' }}>
          {selectedOption === 'delivery' ? (
            <NearbyVendorsPage searchQuery={searchQuery} />
          ) : (
            <NearbyPickupPage searchQuery={searchQuery} />
          )}
        </div>
      </div>
    </div>
  );
};

export default DeliveryPickupWrapper;
