import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Plus, X } from 'lucide-react';
import CreateProductModal from './CreateProductModal';

const AddProductPage = () => {
  const { auth } = useAuth();

  // Core state
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Variations state
  const [variations, setVariations] = useState([]);
  const [invalidSalePrices, setInvalidSalePrices] = useState([]);

  // Strain autocomplete state
  const [availableStrains, setAvailableStrains] = useState([]);
  const [filteredStrains, setFilteredStrains] = useState([]);
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [activeDropdownIndex, setActiveDropdownIndex] = useState(null);

  // Modal state
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [searchAttempted, setSearchAttempted] = useState(false);

  // Fetch products when brand or category changes
  useEffect(() => {
    if (brand) {
      setSearchAttempted(true);
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
      setSearchAttempted(false);
    }
  }, [brand, category]);

  // Fetch available strains
  useEffect(() => {
    fetch('/api/strains')
      .then((response) => response.json())
      .then((data) => {
        setAvailableStrains(data);
        setFilteredStrains(data);
      })
      .catch((error) => console.error('Error fetching strains:', error));
  }, []);

  // Handlers for product selection and variation management
  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setBrand(product.brand);
    setVariations([{
      amount: '',
      strain: '',
      price: '',
      salePrice: '',
      thcContent: '',
      stock: '',
      image: product.images?.[0] || '',
    }]);
  };

  const handleAddVariation = () => {
    setVariations(prev => [
      ...prev,
      {
        amount: '',
        strain: '',
        price: '',
        salePrice: '',
        thcContent: '',
        stock: '',
        image: selectedProduct?.images?.[0] || '',
      }
    ]);
  };

  const handleRemoveVariation = (index) => {
    setVariations(prev => prev.filter((_, i) => i !== index));
  };

  const handleVariationChange = (index, key, value) => {
    setVariations(prev => prev.map((v, i) => 
      i === index ? { ...v, [key]: value } : v
    ));

    if (key === 'strain' && value.trim()) {
      setFilteredStrains(
        availableStrains.filter(strain =>
          strain.name.toLowerCase().includes(value.toLowerCase())
        )
      );
      setActiveDropdownIndex(index);
      setDropdownVisible(true);
    }
  };

  const handleSelectStrain = (index, strain) => {
    handleVariationChange(
      index,
      'strain',
      `${strain.name} (${strain.classification.substring(0, 3).toUpperCase()})`
    );
    setDropdownVisible(false);
    setActiveDropdownIndex(null);
  };

  const handleImageSelect = (index, image) => {
    handleVariationChange(index, 'image', image);
  };

  // Handler for adding product to inventory
  const handleAddToInventory = async () => {
    setInvalidSalePrices([]);

    // Validation
    if (!selectedProduct || variations.some(v => 
      !v.amount || !v.strain || !v.price || !v.stock || !v.image
    )) {
      alert('Please fill out all required fields for each variation.');
      return;
    }

    // Validate sale prices
    const invalidIndices = variations.reduce((acc, variation, index) => {
      if (variation.salePrice && parseFloat(variation.salePrice) >= parseFloat(variation.price)) {
        acc.push(index);
      }
      return acc;
    }, []);

    if (invalidIndices.length > 0) {
      setInvalidSalePrices(invalidIndices);
      alert('Sale price must be less than regular price.');
      return;
    }

    try {
      await Promise.all(variations.map(variation =>
        fetch('/api/vendor/inventory', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            vendorId: auth.vendorId,
            productId: selectedProduct._id,
            variation: {
              amount: variation.amount,
              strain: variation.strain,
              thcContent: variation.thcContent ? parseFloat(variation.thcContent) : undefined,
              price: parseFloat(variation.price),
              salePrice: variation.salePrice ? parseFloat(variation.salePrice) : undefined,
              stock: parseInt(variation.stock, 10),
              image: variation.image,
            },
            name: selectedProduct.name,
            category: selectedProduct.category,
            brand: selectedProduct.brand,
            description: selectedProduct.description,
          }),
        })
      ));

      alert('Products added successfully!');
      resetForm();
    } catch (error) {
      console.error('Error adding products:', error);
      alert('An error occurred while adding the products.');
    }
  };

  const resetForm = () => {
    setBrand('');
    setCategory('Flower');
    setSelectedProduct(null);
    setVariations([]);
    setInvalidSalePrices([]);
    setSearchAttempted(false);
  };

  // Handler for creating new product
  const handleCreateProduct = async (formData) => {
    try {
      // Create a FormData object to handle file uploads
      const form = new FormData();
      form.append('brand', formData.brand);
      form.append('name', formData.name);
      form.append('category', formData.category);
      form.append('description', formData.description);
      form.append('amount', JSON.stringify(formData.variations.map(v => v.amount)));

      // Append each image file
      formData.variations.forEach((variation, index) => {
        if (variation.image) {
          form.append('images', variation.image);
        }
      });

      const productResponse = await fetch('/api/products', {
        method: 'POST',
        body: form, // Send form data instead of JSON
        // Remove Content-Type header - browser will set it automatically with boundary
      });

      if (!productResponse.ok) throw new Error('Failed to create product');

      const newProduct = await productResponse.json();

      // Continue with adding to inventory...
      await Promise.all(formData.variations.map(variation =>
        fetch('/api/vendor/inventory', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            vendorId: auth.vendorId,
            productId: newProduct._id,
            variation: {
              amount: variation.amount,
              strain: variation.strain,
              thcContent: variation.thcContent ? parseFloat(variation.thcContent) : undefined,
              price: parseFloat(variation.price),
              salePrice: variation.salePrice ? parseFloat(variation.salePrice) : undefined,
              stock: parseInt(variation.stock, 10),
              image: variation.image
            },
            name: formData.name,
            category: formData.category,
            brand: formData.brand,
            description: formData.description,
          }),
        })
      ));

      alert('Product created and added to inventory successfully!');
      setIsCreateModalOpen(false);
      resetForm();
    } catch (error) {
      console.error('Error creating product:', error);
      alert('An error occurred while creating the product.');
    }
  };

  return (
    <div className="container mt-4">
      <h3>Add Product to Menu</h3>

      {/* Category Selection */}
      <div className="mb-3">
        <label className="form-label fw-semibold">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Tincture">Tincture</option>
          <option value="Gear">Gear</option>
        </select>
      </div>

      {/* Brand Input */}
      <div className="mb-3">
        <label className="form-label fw-semibold">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {/* Create New Product Alert */}
      {searchAttempted && products.length === 0 && (
        <div className="alert alert-info d-flex align-items-center justify-content-between">
          <span>No matching products found. Would you like to create a new product?</span>
          <button
            className="btn btn-primary d-flex align-items-center"
            onClick={() => setIsCreateModalOpen(true)}
          >
            <Plus className="w-5 h-5 mr-2" />
            Create New Product
          </button>
        </div>
      )}

      {/* Product List */}
      {products.length > 0 && (
        <div className="mb-3">
          <label className="form-label fw-semibold">Matching Products</label>
          <ul className="list-group">
            {products.map((product) => (
              <li
                key={product._id}
                className={`list-group-item ${selectedProduct?._id === product._id ? 'active' : ''}`}
                onClick={() => handleProductSelect(product)}
                style={{ cursor: 'pointer' }}
              >
                {product.images?.[0] && (
                  <img
                    src={product.images[0]}
                    alt={product.name}
                    style={{ width: '40px', height: '40px', marginRight: '10px' }}
                  />
                )}
                <strong>{product.brand}</strong> - {product.name}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Variations Section */}
      {selectedProduct && (
        <>
          <h3>Variations</h3>
          {variations.map((variation, index) => (
            <div
              key={index}
              className="mb-8 p-4 bg-gray-50 rounded-lg"
            >
              <div className="flex justify-between items-center mb-4">
                <h4 className="text-md font-medium">Variation #{index + 1}</h4>
                {index > 0 && (
                  <button
                    type="button"
                    onClick={() => handleRemoveVariation(index)}
                    className="text-red-500 hover:text-red-700"
                  >
                    <X className="h-5 w-5" />
                  </button>
                )}
              </div>

              {/* Image Selection */}
              <div className="mb-3">
                <label className="form-label fw-semibold">Select Image</label>
                <div className="d-flex overflow-auto" style={{ maxWidth: '100%', gap: '10px' }}>
                  {selectedProduct.images.map((image, imgIndex) => (
                    <img
                      key={imgIndex}
                      src={image}
                      alt={`Product Image ${imgIndex + 1}`}
                      onClick={() => handleImageSelect(index, image)}
                      className={`img-thumbnail ${variation.image === image ? 'border-primary' : ''}`}
                      style={{
                        cursor: 'pointer',
                        width: '250px',
                        height: '250px',
                        objectFit: 'cover',
                        borderWidth: variation.image === image ? '3px' : '1px',
                      }}
                    />
                  ))}
                </div>
              </div>

              {/* Amount Selection */}
              {category !== 'Gear' && (
                <div className="mb-3">
                  <label className="form-label fw-semibold">Amount</label>
                  <select
                    className="form-select"
                    value={variation.amount}
                    onChange={(e) => handleVariationChange(index, 'amount', e.target.value)}
                  >
                    <option value="" disabled>Select Amount</option>
                    {selectedProduct?.amount?.map((option, i) => (
                      <option key={i} value={option}>{option}</option>
                    ))}
                  </select>
                </div>
              )}

              {/* Strain Input */}
              <div className="mb-3 position-relative">
                <label className="form-label fw-semibold">Strain</label>
                <input
                  type="text"
                  className="form-control"
                  value={variation.strain}
                  onChange={(e) => handleVariationChange(index, 'strain', e.target.value)}
                  onFocus={() => {
                    if (variation.strain.trim()) {
                      setActiveDropdownIndex(index);
                      setDropdownVisible(true);
                    }
                  }}
                  onBlur={() => setTimeout(() => {
                    setDropdownVisible(false);
                    setActiveDropdownIndex(null);
                  }, 100)}
                />
                {index === activeDropdownIndex && dropdownVisible && filteredStrains.length > 0 && (
                  <ul className="dropdown-menu show position-absolute" style={{ zIndex: 10 }}>
                    {filteredStrains.map((strain, i) => (
                      <li
                        key={i}
                        className="dropdown-item"
                        onMouseDown={() => handleSelectStrain(index, strain)}
                      >
                        {strain.name} ({strain.classification.substring(0, 3).toUpperCase()})
                      </li>
                    ))}
                  </ul>
                )}
              </div>

              {/* Price Fields */}
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-semibold">Price ($)</label>
                  <input
                    type="number"
                    className="form-control"
                    value={variation.price}
                    onChange={(e) => handleVariationChange(index, 'price', e.target.value)}
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-semibold">Sale Price ($)</label>
                  <input
                    type="number"
                    className={`form-control ${invalidSalePrices.includes(index) ? 'is-invalid' : ''}`}
                    value={variation.salePrice}
                    onChange={(e) => handleVariationChange(index, 'salePrice', e.target.value)}
                  />
                  {invalidSalePrices.includes(index) && (
                    <div className="invalid-feedback">
                      Sale price must be less than regular price
                    </div>
                  )}
                </div>
              </div>

              {/* THC Content */}
              <div className="mb-3">
                <label className="form-label fw-semibold">THC Content (%)</label>
                <input
                  type="text"
                  className="form-control"
                  value={variation.thcContent}
                  onChange={(e) => handleVariationChange(
                    index,
                    'thcContent',
                    e.target.value.replace(/[^0-9.]/g, '')
                  )}
                />
              </div>

              {/* Stock */}
              <div className="mb-3">
                <label className="form-label fw-semibold">Stock</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.stock}
                  onChange={(e) => handleVariationChange(index, 'stock', e.target.value)}
                />
              </div>
            </div>
          ))}

          {/* Add Variation & Submit Buttons */}
          <div className="d-flex flex-column align-items-start gap-4">
            <button 
              className="btn btn-secondary shadow-sm" 
              onClick={handleAddVariation}
              style={{
                width: '150px',
                height: '150px',
                backgroundColor: '#DBDBDB',
                color: '#333333',
                fontSize: '1.2rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '1px',
                borderRadius: '8px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                transition: 'transform 0.2s',
              }}
              onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              <Plus className="w-5 h-5 mr-2" />
              Add Variation
            </button>

            <button 
              className="btn btn-primary rounded-pill shadow-sm w-100 mb-5" 
              onClick={handleAddToInventory}
              style={{
                backgroundColor: '#0056b3',
                padding: '15px 30px',
                fontSize: '1.2rem',
                border: 'none',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                transition: 'transform 0.2s',
              }}
              onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              Add to Menu
            </button>
          </div>
        </>
      )}

      {/* Create Product Modal */}
      <CreateProductModal
  show={isCreateModalOpen}
  onClose={() => setIsCreateModalOpen(false)}
  onSubmit={handleCreateProduct}
/>

      {/* Bottom Spacing */}
      <div style={{height: '300px'}}></div>
    </div>
  );
};

export default AddProductPage;
