import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60; // Start at 6:00 AM in minutes
  const end = 24 * 60; // End at midnight (24:00)

  while (time < end) {
    const hours = Math.floor(time / 60);
    const minutes = time % 60;
    const label = `${hours % 12 || 12}:${minutes.toString().padStart(2, '0')} ${hours < 12 ? 'AM' : 'PM'}`;
    slots.push(label);
    time += 30; // Increment by 30 minutes
  }
  return slots;
};

const BusinessHoursComponent = ({ initialHours, onUpdate, availableTimes }) => {
  const [businessHours, setBusinessHours] = useState(initialHours);

  const handleTimeChange = (day, field, value) => {
    setBusinessHours((prev) => ({
      ...prev,
      [day]: { ...prev[day], [field]: value },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(businessHours);
  };

  return (
    <Form onSubmit={handleSubmit}>
      {Object.keys(businessHours).map((day) => (
        <div key={day} className="mb-3">
          <h5 className="text-capitalize">{day}</h5>
          <div className="d-flex">
            <Form.Group className="me-3">
              <Form.Label>Open</Form.Label>
              <Form.Select
                value={businessHours[day]?.open || ''}
                onChange={(e) => handleTimeChange(day, 'open', e.target.value)}
              >
                <option value="">Select Time</option>
                {availableTimes.map((time) => (
                  <option key={time} value={time}>
                    {time}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Form.Group>
              <Form.Label>Close</Form.Label>
              <Form.Select
                value={businessHours[day]?.close || ''}
                onChange={(e) => handleTimeChange(day, 'close', e.target.value)}
              >
                <option value="">Select Time</option>
                {availableTimes.map((time) => (
                  <option key={time} value={time}>
                    {time}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
          </div>
        </div>
      ))}
      <Button variant="primary" type="submit">
        Update Hours
      </Button>
    </Form>
  );
};

export default BusinessHoursComponent;
