import React, { createContext, useState, useEffect } from "react";

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Fetch vendor data based on vendorId
  const fetchVendorData = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/api/vendors/${id}`);
      const data = await response.json();
      if (response.ok) {
        setVendor(data.data); // Update according to API response
      } else {
        throw new Error(data.message || "Failed to load vendor data.");
      }
    } catch (err) {
      setError(err.message || "Error fetching vendor data.");
    }
  };

  // Fetch products for the specific vendor
  const fetchProductsData = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5001/api/products/vendor/${id}/products`
      );
      const data = await response.json();
      if (response.ok) {
        setProducts(data.data.products); // Update according to API response
      } else {
        throw new Error(data.message || "Failed to load products data.");
      }
    } catch (err) {
      setError(err.message || "Error fetching products data.");
    }
  };

  // Fetch data when vendorId changes
  useEffect(() => {
    if (vendorId) {
      setError(null);
      setIsLoading(true);
      Promise.all([fetchVendorData(vendorId), fetchProductsData(vendorId)])
        .catch((err) => setError(err.message))
        .finally(() => setIsLoading(false));
    }
  }, [vendorId]);

  return (
    <VendorContext.Provider value={{ vendor, products, error, isLoading }}>
      {children}
    </VendorContext.Provider>
  );
};
