import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCard from './ProductCard';
import axios from 'axios';

const MenuDisplay = ({ vendorId, selectedCategory, onProductsFetched }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch inventory from backend
  useEffect(() => {
    const fetchVendorInventory = async () => {
      try {
        const response = await axios.get(`/api/vendor/inventory/${vendorId}`);
        setProducts(response.data); // Save fetched inventory
        if (onProductsFetched) onProductsFetched(response.data); // Pass to parent if needed
        setLoading(false);
      } catch (err) {
        console.error('Error fetching inventory:', err);
        setError('Failed to load products');
        setLoading(false);
      }
    };

    fetchVendorInventory();
  }, [vendorId]);

  // Group products by category
  const productsByCategory = products.reduce((categories, product) => {
    const category = product.category || 'Uncategorized';
    if (!categories[category]) {
      categories[category] = [];
    }
    categories[category].push(product);
    return categories;
  }, {});

  // Filter products by selected category
  const filteredProductsByCategory =
    selectedCategory === 'All'
      ? productsByCategory
      : { [selectedCategory]: productsByCategory[selectedCategory] || [] };

  // Render loading/error states
  if (loading) return <p>Loading inventory...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div
      style={{
        backgroundColor: '#FDFDFD',
        padding: '2rem 1rem',
        borderRadius: '8px',
        position: 'relative',
        overflow: 'hidden',
      }}
    >
      <Container fluid style={{ paddingLeft: '15px', paddingRight: '15px' }}>
        {Object.keys(filteredProductsByCategory).map((category) => (
          <div key={category} style={{ marginBottom: '2rem' }}>
            {/* Category Heading */}
            <h5
              style={{
                fontWeight: 'bold',
                marginBottom: '1rem',
                textTransform: 'uppercase',
                borderBottom: '2px solid #ddd',
                paddingBottom: '0.5rem',
              }}
            >
              {category}
            </h5>
            {/* Products Row */}
            <Row>
              {filteredProductsByCategory[category].map((product) => (
                <Col
                  key={product._id}
                  xs={12}
                  sm={6}
                  md={4}
                  lg={3}
                  xl={2}
                  className="mb-3"
                >
                  <ProductCard product={product} />
                </Col>
              ))}
            </Row>
          </div>
        ))}
      </Container>
    </div>
  );
};

export default MenuDisplay;
