import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext'; // Adjust path as necessary

const ProductCard = ({ product, vendorName, logoUrl }) => {
  console.log('Product Data:', product);
  const { addToCart } = useContext(CartContext);

  return (
    <div
      className="card product-card shadow-sm"
      style={{
        width: '14rem',
        position: 'relative',
        paddingTop: product.variation?.salePrice ? '2rem' : '0', // Add padding for SALE banner
      }}
    >
      {/* SALE Banner */}
      {product.variation?.salePrice && (
        <div
          className="position-absolute top-0 start-0 w-100 text-center"
          style={{
            backgroundColor: '#FF0080',
            color: 'white',
            fontSize: '0.8rem',
            padding: '0.3rem 0',
            fontWeight: 'bold',
          }}
        >
          SALE
        </div>
      )}

      {/* Card Body */}
      <div className="card-body d-flex flex-column justify-content-between">
        {/* Product Info Header */}
        <div className="d-flex justify-content-between">
          <span className="text-muted" style={{ fontSize: '0.8rem' }}>
            {product.category}
          </span>
          <span className="badge bg-light text-dark" style={{ fontSize: '0.75rem' }}>
            {product.variation?.amount || 'N/A'}
          </span>
        </div>

        {/* Product Image */}
        <div className="text-center my-1">
          <img
            src={product.variation?.image || '/placeholder-image.png'}
            alt={product.name || 'Product Image'}
            className="img-fluid"
            style={{ maxHeight: '120px', objectFit: 'contain' }}
          />
        </div>

        {/* Product Info */}
        <div className="text-start">
          {/* Brand */}
          <p className="fw-bold text-center mb-1" style={{ fontSize: '0.85rem' }}>{product.brand}</p>
          {/* Name */}
          <p className="text-dark text-center mb-1" style={{ fontSize: '1rem', fontWeight: 'normal' }}>
            {product.name}
          </p>
          {/* Strain */}
          <p className="text-muted text-center" style={{ fontSize: '0.9rem' }}>
            {product.variation?.strain}{' '}
            {product.variation?.classification === 'Sativa' && '(SAT)'}
            {product.variation?.classification === 'Hybrid' && '(HYB)'}
            {product.variation?.classification === 'Indica' && '(IND)'}
          </p>
        </div>

        {/* Footer Section */}
        <div className="d-flex justify-content-between align-items-center mt-2">
          {/* Price Section */}
          <div>
            {product.variation?.salePrice ? (
              <>
                <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                  ${product.variation.salePrice?.toFixed(2) || 'N/A'}
                </span>{' '}
                <span
                  style={{
                    textDecoration: 'line-through',
                    color: 'gray',
                    fontSize: '0.85rem',
                  }}
                >
                  ${product.variation.price?.toFixed(2) || 'N/A'}
                </span>
              </>
            ) : (
              <span style={{ fontWeight: 'bold' }}>
                ${product.variation.price?.toFixed(2) || 'N/A'}
              </span>
            )}
          </div>

          {/* Add to Cart Button */}
          <button
            className="btn btn-primary rounded-circle"
            style={{
              width: '2.5rem',
              height: '2.5rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() =>
              addToCart({
                ...product,
                price: product.variation?.price || 0, // Default to 0 if undefined
                salePrice: product.variation?.salePrice || null, // Use null or 0
                vendorName,
                logoUrl,
              })
            }
          >
            <i className="bi bi-plus-lg"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
