import React, { useState } from 'react';
import { Mail, User, MessageSquare, Send } from 'lucide-react';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    from_name: '',
    to_name: '',
    message: '',
    reply_to: ''
  });
  const [status, setStatus] = useState('idle'); // idle, sending, success, error
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('sending');

    try {
      await window.emailjs.sendForm(
        'default_service',
        'template_jdinw0g',
        e.target,
        'hW_eDezKFNuokT_2c'
      );
      setStatus('success');
      setFormData({
        from_name: '',
        to_name: '',
        message: '',
        reply_to: ''
      });
      setTimeout(() => setStatus('idle'), 3000);
    } catch (error) {
      setStatus('error');
      setErrorMessage(error.text || 'Failed to send message');
      setTimeout(() => setStatus('idle'), 3000);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Contact Us</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="relative">
          <div className="absolute left-3 top-3 text-gray-400">
            <User size={20} />
          </div>
          <input
            type="text"
            name="from_name"
            value={formData.from_name}
            onChange={handleChange}
            placeholder="Your Name"
            className="w-full pl-10 pr-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        <div className="relative">
          <div className="absolute left-3 top-3 text-gray-400">
            <User size={20} />
          </div>
          <input
            type="text"
            name="to_name"
            value={formData.to_name}
            onChange={handleChange}
            placeholder="Recipient Name"
            className="w-full pl-10 pr-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        <div className="relative">
          <div className="absolute left-3 top-3 text-gray-400">
            <Mail size={20} />
          </div>
          <input
            type="email"
            name="reply_to"
            value={formData.reply_to}
            onChange={handleChange}
            placeholder="Your Email"
            className="w-full pl-10 pr-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        <div className="relative">
          <div className="absolute left-3 top-3 text-gray-400">
            <MessageSquare size={20} />
          </div>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            placeholder="Your Message"
            className="w-full pl-10 pr-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 min-h-32"
            required
          />
        </div>

        <button
          type="submit"
          disabled={status === 'sending'}
          className={`w-full py-2 px-4 rounded-md flex items-center justify-center gap-2 ${
            status === 'sending'
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-600'
          } text-white transition-colors duration-200`}
        >
          <Send size={20} />
          {status === 'sending' ? 'Sending...' : 'Send Message'}
        </button>

        {status === 'success' && (
          <div className="text-green-500 text-center">Message sent successfully!</div>
        )}

        {status === 'error' && (
          <div className="text-red-500 text-center">{errorMessage}</div>
        )}
      </form>
    </div>
  );
};

export default ContactForm;
