import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { vendor } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [customDetails, setCustomDetails] = useState({ price: '', stock: '', weight: '' });

  // Fetch products by brand
  useEffect(() => {
    if (brand) {
      fetch(`/api/products/brand/${brand}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand]);

  const handleAddToInventory = () => {
    if (!selectedProduct || !customDetails.price || !customDetails.stock || !customDetails.weight) {
      alert('Please fill out all fields');
      return;
    }

    fetch('/api/vendor/inventory', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        vendorId: vendor.vendorId, // Automatically include vendorId from context
        productId: selectedProduct._id,
        price: customDetails.price,
        stock: customDetails.stock,
        weight: customDetails.weight,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setBrand('');
        setSelectedProduct(null);
        setCustomDetails({ price: '', stock: '', weight: '' });
      })
      .catch((error) => console.error('Error adding product to inventory:', error));
  };

  return (
    <div>
      <h1>Add Products to Your Menu</h1>

      {/* Brand Input */}
      <div>
        <label>Enter Brand:</label>
        <input
          type="text"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
          placeholder="e.g., Brand A"
        />
      </div>

      {/* Display Products */}
      <div>
        <h2>Products from {brand}</h2>
        {products.map((product) => (
          <div
            key={product._id}
            style={{
              border: selectedProduct?._id === product._id ? '2px solid green' : '1px solid gray',
            }}
            onClick={() => setSelectedProduct(product)}
          >
            <img src={product.imageUrl} alt={product.name} width="100" />
            <h3>{product.name}</h3>
            <p>Category: {product.category}</p>
            <p>Classification: {product.classification}</p>
            <p>Strain: {product.strain}</p>
            <p>{product.description}</p>
          </div>
        ))}
      </div>

      {/* Customization Form */}
      {selectedProduct && (
        <div>
          <h2>Customize {selectedProduct.name}</h2>
          <input
            type="number"
            placeholder="Price"
            value={customDetails.price}
            onChange={(e) => setCustomDetails({ ...customDetails, price: e.target.value })}
          />
          <input
            type="number"
            placeholder="Stock"
            value={customDetails.stock}
            onChange={(e) => setCustomDetails({ ...customDetails, stock: e.target.value })}
          />
          <input
            type="text"
            placeholder="Weight (e.g., 1g, 3.5g)"
            value={customDetails.weight}
            onChange={(e) => setCustomDetails({ ...customDetails, weight: e.target.value })}
          />
          <button onClick={handleAddToInventory}>Add to Inventory</button>
        </div>
      )}
    </div>
  );
};

export default AddProductPage;
