import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { 
    calculateTotal, 
    cartItems, 
    incrementItem, 
    decrementItem, 
    removeFromCart, 
    clearCart, 
  } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    day: '',
    month: '',
    year: '',
  });

  const [savedAddress, setSavedAddress] = useState(''); // For the user's saved address
  const [isEditing, setIsEditing] = useState(false);
  const [newAddress, setNewAddress] = useState(''); // For editing the address

  const navigate = useNavigate();

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  // Load address from localStorage on component mount
  useEffect(() => {
    const address = localStorage.getItem('userAddress'); // Use the correct key
    if (address) {
      setSavedAddress(address); // Set saved address
    }
  }, []);

  const handleSaveAddress = () => {
    localStorage.setItem('userAddress', newAddress); // Save the new address
    setSavedAddress(newAddress); // Update the displayed address
    setIsEditing(false); // Exit editing mode
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      return;
    }
    if (!savedAddress) {
      alert('Please add a delivery address.');
      return;
    }

    const birthdate = `${contactInfo.year}-${contactInfo.month.padStart(2, '0')}-${contactInfo.day.padStart(2, '0')}`;

    const orderDetails = {
      type: 'DELIVERY',
      order_source: 'ECOMMERCE',
      order_status: 'AWAITING_PROCESSING',
      delivery_address: savedAddress, // Use saved address
      items: cartItems.map((item) => ({
        productId: item.productId || item._id,
        name: item.name,
        category: item.category,
        quantity: item.quantity,
      })),
    };

    const payload = {
      phone: contactInfo.phone,
      firstName: contactInfo.firstName,
      lastName: contactInfo.lastName,
      email: contactInfo.email,
      birthday: birthdate,
      totalAmount: estimatedTotal,
      vendorId: cartItems[0]?.vendorId, 
      orderDetails,
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        navigate(`/order-confirmation/${result.order._id}`);
        clearCart();
      } else {
        const error = await response.json();
        alert(`Failed to place order: ${error.message}`);
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="place-order-section p-4 bg-light">
      {/* Delivery Address Section */}
      <div className="mb-4">
        <h4>Delivery Address</h4>
        {isEditing ? (
          <Form>
            <Form.Group controlId="address" className="mb-3">
              <Form.Label>Delivery Address</Form.Label>
              <Form.Control
                type="text"
                value={newAddress}
                onChange={(e) => setNewAddress(e.target.value)}
                placeholder="Enter your address"
                required
              />
            </Form.Group>
            <Button variant="success" onClick={handleSaveAddress}>Save Address</Button>
          </Form>
        ) : (
          <Card>
            <Card.Body>
              <Card.Text>
                <strong>Address:</strong> {savedAddress || 'N/A'}
              </Card.Text>
              <Button variant="primary" onClick={() => {
                setNewAddress(savedAddress); // Pre-fill with saved address
                setIsEditing(true); // Enter editing mode
              }}>
                Edit
              </Button>
            </Card.Body>
          </Card>
        )}
      </div>

      {/* Contact Information Section */}
      <div className="mb-4">
        <h4>Contact Information</h4>
        <Form>
          <Row className="mb-3">
            <Col>
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={contactInfo.firstName}
                onChange={(e) => setContactInfo({ ...contactInfo, firstName: e.target.value })}
                required
              />
            </Col>
            <Col>
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={contactInfo.lastName}
                onChange={(e) => setContactInfo({ ...contactInfo, lastName: e.target.value })}
                required
              />
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={contactInfo.email}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                name="phone"
                value={contactInfo.phone}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Order Summary Section */}
      <div className="mb-4">
        <h4>Order Summary</h4>
        <Card>
          <Card.Body>
            {cartItems.length > 0 ? (
              cartItems.map((item) => (
                <div key={item.productId} className="d-flex justify-content-between align-items-center mb-3">
                  <div>{item.name}</div>
                  <div>{item.quantity} x ${item.price.toFixed(2)}</div>
                </div>
              ))
            ) : (
              <p>Your cart is empty.</p>
            )}
            <hr />
            <div className="d-flex justify-content-between">
              <strong>Total</strong>
              <span>${estimatedTotal.toFixed(2)}</span>
            </div>
          </Card.Body>
        </Card>
      </div>

      {/* Place Order Button */}
      <Button variant="primary" className="w-100" onClick={handlePlaceOrder}>
        Place Order
      </Button>
    </div>
  );
};

export default TestPlaceOrder;
