import React, { createContext, useState, useEffect } from 'react';

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);




  const fetchVendorAndProducts = async (id) => {
    setLoading(true);
    setError(null);

    try {
      const [vendorResponse, productsResponse] = await Promise.all([
        fetch(`http://localhost:5001/api/vendors/${id}`),
        fetch(`http://localhost:5001/api/vendor/inventory/${id}`)
      ]);


      const vendorData = await vendorResponse.json();
      const productsData = await productsResponse.json();

      setVendor(vendorData.data);
      setProducts(productsData.data.products);
    } catch (err) {
      setError('Failed to load vendor or products data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (vendorId) {
      fetchVendorAndProducts(vendorId);
    }
  }, [vendorId]);

  return (
    <VendorContext.Provider value={{ vendor, products, loading, error }}>
      {children}
    </VendorContext.Provider>
  );
};
