import React from 'react';
import { useLoadScript } from '@react-google-maps/api';
import { Tabs, Tab } from 'react-bootstrap';
import RecentOrders from './RecentOrders';
import InventoryComponent from './InventoryComponent';
import LogoutButton from './LogoutButton';
import UpdateDeliveryZoneComponent from './UpdateDeliveryZoneComponent';

const libraries = ['places'];

const VendorDashboard = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyBKb82KnQEbd1OGJxY08DbvsmRcz33h48w',
    libraries,
  });

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  const [initialHours, setInitialHours] = useState(null);

  useEffect(() => {
    const fetchBusinessHours = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/vendors/profile', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setInitialHours(data.data.businessHours);
    };
    fetchBusinessHours();
  }, []);

  const handleUpdate = async (updatedHours) => {
    const token = localStorage.getItem('token');
    const response = await fetch('/api/vendors/business-hours', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ businessHours: updatedHours }),
    });

    if (response.ok) {
      alert('Business hours updated successfully!');
    } else {
      alert('Failed to update business hours.');
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center m-4">
        <h2 className="flex-grow-1 text-center">Vendor Dashboard</h2>
        <LogoutButton />
      </div>
      <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mb-3">
        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>
        <Tab eventKey="inventory" title="Inventory">
          <InventoryComponent />
        </Tab>
        <Tab eventKey="profile" title="Profile">
          <div>
            <h2>Vendor Profile</h2>
            <UpdateDeliveryZoneComponent isLoaded={isLoaded} loadError={loadError} />

          </div>
        </Tab>
      </Tabs>
    </div>
  );
};

export default VendorDashboard;
