import React, { useState, useContext } from 'react';
import { Button, Row, Col, Form, Card } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const PlaceOrder = () => {
  const { calculateTotal, cartItems, incrementItem, decrementItem, removeFromCart } = useContext(CartContext);
  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    birthdate: '',
    email: '',
    phone: '',
  });

  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress({ ...deliveryAddress, [name]: value });
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      return;
    }
    if (!deliveryAddress.street || !deliveryAddress.city || !deliveryAddress.state || !deliveryAddress.zip) {
      alert('Please fill out all delivery address fields.');
      return;
    }

    const vendorId = 'someVendorId'; // Replace with dynamic vendor selection if applicable

    const orderDetails = {
      vendorId,
      customerData: contactInfo,
      deliveryAddress,
      products: cartItems.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        sizeId: item.sizeId || null,
        locationName: item.locationName || 'FRONT_OF_HOUSE',
      })),
      totalAmount: estimatedTotal.toFixed(2),
    };

    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderDetails),
      });

      if (response.ok) {
        alert('Order placed successfully! Vendors have been notified.');
      } else {
        alert('Failed to place order. Please try again.');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="place-order-section p-4 bg-light" style={{
      backgroundColor: '#f8f9fa',
      margin: '20px',
      borderRadius: '8px',
    }}>
      {/* Delivery Address Section */}
      <div className="mb-4">
        <h4>Delivery Address</h4>
        <Form>
          <Form.Group controlId="street" className="mb-3">
            <Form.Label>Street Address</Form.Label>
            <Form.Control
              type="text"
              name="street"
              value={deliveryAddress.street}
              onChange={handleAddressChange}
              required
            />
          </Form.Group>
          <Row className="mb-3">
            <Col>
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                name="city"
                value={deliveryAddress.city}
                onChange={handleAddressChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>State</Form.Label>
              <Form.Control
                as="select"
                name="state"
                value={deliveryAddress.state}
                onChange={handleAddressChange}
                required
              >
                <option value="">Select</option>
                {['CA', 'CO', 'NY', 'FL', 'NV', 'OR', 'NJ'].map((state) => (
                  <option key={state} value={state}>{state}</option>
                ))}
              </Form.Control>
            </Col>
            <Col>
              <Form.Label>ZIP Code</Form.Label>
              <Form.Control
                type="text"
                name="zip"
                value={deliveryAddress.zip}
                onChange={handleAddressChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Contact Information Section */}
      <div className="mb-4">
        <h4>Contact Information</h4>
        <Form>
          <Row className="mb-3">
            <Col>
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={contactInfo.firstName}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={contactInfo.lastName}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={contactInfo.email}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                name="phone"
                value={contactInfo.phone}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Order Summary Section */}
      <div className="mb-4">
        <h4>Order Summary</h4>
        <Card className="cart-summary-card mb-1" style={{
          border: 'none',
          backgroundColor: '#f8f9fa',
          margin: '20px',
          borderRadius: '8px',
        }}>
          <Card.Body>
            {cartItems.length > 0 ? (
              <>
                {cartItems.map((item) => (
                  <div key={item.productId} className="d-flex justify-content-between align-items-center mb-3">
                    <div className="d-flex align-items-center">
                      <img
                        src={item.primaryImage}
                        alt={item.name}
                        className="img-fluid"
                        style={{ width: '40px', height: '40px', marginRight: '10px' }}
                      />
                      <div>
                        <div>{item.name}</div>
                        <div>
                          ${item.price.toFixed(2)}
                          <a
                            href="#"
                            className="text-muted small"
                            onClick={(e) => {
                              e.preventDefault();
                              removeFromCart(item.productId);
                            }}
                            style={{ textDecoration: 'underline', marginLeft: '5px' }}
                          >
                            remove
                          </a>
                        </div>
                      </div>
                    </div>
                    <div className="d-flex align-items-center">
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => decrementItem(item.productId)}
                        style={{ marginRight: '5px' }}
                      >
                        -
                      </Button>
                      <span className="mx-2">{item.quantity}</span>
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => incrementItem(item.productId)}
                        style={{ marginLeft: '5px' }}
                      >
                        +
                      </Button>
                    </div>
                  </div>
                ))}
                <div className="d-flex justify-content-between mt-3">
                  <strong>Subtotal</strong>
                  <span>${subtotal.toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between mt-3">
                  <strong>Estimated Total w/ Taxes</strong>
                  <span>${estimatedTotal.toFixed(2)}</span>
                </div>
              </>
            ) : (
              <p className="text-center mb-1">Your cart is empty.</p>
            )}
          </Card.Body>
        </Card>
      </div>

      {/* Place Order Button */}
      <Button variant="primary" className="w-100 mb-3" onClick={handlePlaceOrder}>
        Place Order
      </Button>
      <p className="text-muted text-center">
        Please note, vendor will notify you of delivery ETA shortly after placing order.
      </p>
    </div>
  );
};

export default PlaceOrder;
