// src/components/TestDashboard.js
import React, { useState, useEffect, useContext } from 'react';
import { Tab, Tabs, Card, Form, Button, ListGroup } from 'react-bootstrap';
import { FaCog, FaClipboardList, FaBullhorn } from 'react-icons/fa';
import { AuthContext } from '../context/AuthContext'; // Import AuthContext
import { useNavigate } from 'react-router-dom';
import ProfileUpdateForm from './ProfileUpdateForm';
import axios from '../api/api';

const TestDashboard = () => {
  const { logout, user } = useContext(AuthContext); // Access logout and user info
  const [orders, setOrders] = useState([]);
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    licenseNumber: '',
  });
  const [notice, setNotice] = useState('');
  const navigate = useNavigate();

  // Fetch vendor orders and profile information
  useEffect(() => {
    const fetchOrders = async (vendorId) => {
      try {
        const response = await axios.get(`/api/orders/vendor/${vendorId}`);
        setOrders(response.data.orders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    const fetchProfile = async () => {
      try {
        const response = await axios.get('/api/vendors/profile');
        setProfile(response.data.profile);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    if (user && user.vendorId) {
      fetchOrders(user.vendorId);
    }
    fetchProfile();
  }, [user]);

  // Handle profile updates
  const handleProfileChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put('/api/vendors/profile', profile);
      alert('Profile updated successfully');
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  // Handle Store Notice submission
  const handleNoticeSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/vendors/store-notice', { notice });
      alert('Store notice updated successfully');
    } catch (error) {
      console.error('Error updating store notice:', error);
    }
  };

  return (
    <Card className="m-0 w-100 d-flex" style={{ margin: '0 auto', padding: '20px' }}>
      <Card.Body>
        <div>
          {/* Logout Link */}
          <Button variant="link" onClick={() => logout(navigate)} style={{ position: 'absolute', top: '10px', left: '10px' }}>
            Logout
          </Button>
        </div>

        <h2 className="text-center">Vendor Dashboard</h2>
        <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mt-4">

          {/* Recent Orders Tab */}
          <Tab eventKey="orders" title={<><FaClipboardList /> Recent Orders</>}>
            <ListGroup className="mt-3">
              {orders.length > 0 ? (
                orders.map((order) => (
                  <ListGroup.Item key={order._id}>
                    <h5>Order #{order._id}</h5>
                    <p>Status: {order.status}</p>
                    <p>Total: ${order.total.toFixed(2)}</p>
                  </ListGroup.Item>
                ))
              ) : (
                <p className="text-center">No orders found.</p>
              )}
            </ListGroup>
          </Tab>

          {/* Settings Tab */}
          <Tab eventKey="settings" title={<><FaCog /> Settings</>}>
            <Form onSubmit={handleProfileSubmit} className="mt-3">
              <Form.Group controlId="profileName" className="mb-3">
                <Form.Label>Vendor Name</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={profile.name}
                  onChange={handleProfileChange}
                  placeholder="Enter vendor name"
                  required
                />
              </Form.Group>
              <Form.Group controlId="profileEmail" className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={profile.email}
                  onChange={handleProfileChange}
                  placeholder="Enter email"
                  required
                />
              </Form.Group>
              <Button type="submit" variant="primary" className="rounded-pill w-100 mt-2">
                Update Profile
              </Button>
            </Form>
          </Tab>

          {/* Store Notice Tab */}
          <Tab eventKey="store-notice" title={<><FaBullhorn /> Store Notice</>}>
            <Form onSubmit={handleNoticeSubmit} className="mt-3">
              <Form.Group controlId="storeNotice" className="mb-3">
                <Form.Label>Store Notice</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={notice}
                  onChange={(e) => setNotice(e.target.value)}
                  placeholder="Enter a notice for your store page..."
                />
              </Form.Group>
              <Button type="submit" variant="primary" className="rounded-pill w-100 mt-2">
                Post Notice
              </Button>
            </Form>
          </Tab>

        </Tabs>
      </Card.Body>
    </Card>
  );
};

export default TestDashboard;
