import React, { useState, useEffect } from "react";
import { Navbar, Container } from "react-bootstrap";
import Typing from "react-typing-animation";

const RegistrationNavBar = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const textData = [
    { heading: "Welcome to WeedHurry!", text: "Your trusted cannabis delivery platform." },
    { heading: "Seamless Ordering", text: "Explore menus and place orders effortlessly." },
    { heading: "Fast & Reliable", text: "Get your order delivered on time, every time." },
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % textData.length);
    }, 10000); // Match delay with animation duration

    return () => clearInterval(interval); // Cleanup on unmount
  }, [textData.length]);

  return (
    <Navbar bg="dark" variant="dark" className="py-3">
      <Container className="d-flex flex-column align-items-center">
        {/* Navbar Logo */}
        <Navbar.Brand href="#" className="d-flex align-items-center">
          <img
            src={require("../assets/images/navbarLogo.png")}
            alt="Logo"
            style={{ height: "60px", width: "auto" }}
            className="mx-auto"
          />
        </Navbar.Brand>

        {/* Typing Animation Section */}
        <div className="text-center mt-3">
          <h1 className="text-white">{textData[currentIndex].heading}</h1>
          <Typing key={`typing-${currentIndex}`} speed={50}>
            <p className="text-muted">{textData[currentIndex].text}</p>
          </Typing>
        </div>
      </Container>
    </Navbar>
  );
};

export default RegistrationNavBar;
