import React, { useState, useEffect, useContext } from 'react';
import { ListGroup, Badge, Offcanvas, Button } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const RecentOrders = () => {
  const { auth } = useContext(AuthContext); // Retrieve vendorId and token
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);

  useEffect(() => {
    const fetchOrders = async () => {
        if (!auth || !auth.vendorId) {
            console.error('Vendor ID is not defined in AuthContext');
            return;
        }

        try {
            const response = await axios.get(`/api/orders/vendor/${auth.vendorId}`, {
                withCredentials: true,
            });
            setOrders(response.data);
        } catch (error) {
            console.error('Error fetching vendor orders:', error);
        }
    };

    fetchOrders();
}, [auth]); // Include auth in the dependency array



  const handleRowClick = (order) => {
    setSelectedOrder(order);
    setShowSlideIn(true);
  };

  const handleStatusChange = async (status) => {
    if (selectedOrder) {
      try {
        await axios.put(`/api/orders/${selectedOrder._id}/status`, { status }, {
          headers: { Authorization: `Bearer ${auth.token}` },
        });
        setOrders((prevOrders) =>
          prevOrders.map((order) =>
            order._id === selectedOrder._id ? { ...order, status } : order
          )
        );
        setShowSlideIn(false);
      } catch (error) {
        console.error('Error updating order status:', error);
      }
    }
  };

  return (
    <div style={{backgroundColor: '#F8F9FA', }}>
      <h2>Recent Orders</h2>
      <ListGroup>
        {orders.map((order) => (
          <ListGroup.Item
            key={order._id}
            className="d-flex justify-content-between align-items-center"
            style={{ cursor: 'pointer' }}
            onClick={() => handleRowClick(order)}
          >
            <div>
              <div className="text-muted">
                {new Date(order.createdAt).toLocaleDateString('en-US', {
                  month: 'short',
                  day: 'numeric',
                })}
              </div>
              <div className="fw-bold">
                {order.contactInfo?.firstName && order.contactInfo?.lastName
                  ? `${order.contactInfo.firstName} ${order.contactInfo.lastName}`
                  : 'Customer Name'}
              </div>
              <Badge
                bg={
                  order.status === 'COMPLETED'
                    ? 'success'
                    : order.status === 'CANCELLED'
                    ? 'danger'
                    : 'warning'
                }
              >
                {order.status.replace('_', ' ')}
              </Badge>
            </div>
            <div>
              <span className="fw-bold me-2">
                ${order.total?.toFixed(2) || '0.00'}
              </span>
              <i className="bi bi-chevron-right fs-4 ms-2"></i>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Slide-in Order Details */}
      {selectedOrder && (
        <Offcanvas
          show={showSlideIn}
          onHide={() => setShowSlideIn(false)}
          placement="end"
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Order Details</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <div className="text-muted mb-3">
              {new Date(selectedOrder.createdAt).toLocaleString()}
            </div>
            <h5>Customer</h5>
            <p>
              {selectedOrder.contactInfo?.firstName &&
              selectedOrder.contactInfo?.lastName
                ? `${selectedOrder.contactInfo.firstName} ${selectedOrder.contactInfo.lastName}`
                : 'Customer Name'}
            </p>
            <h5>Items</h5>
            <ListGroup>
              {selectedOrder.items.map((item, index) => (
                <ListGroup.Item key={index}>
                  {item.name} - ${item.price?.toFixed(2) || 'N/A'} x{' '}
                  {item.quantity}
                </ListGroup.Item>
              ))}
            </ListGroup>
            <div className="d-flex justify-content-between mt-4">
              <Button
                variant="danger"
                onClick={() => handleStatusChange('CANCELLED')}
              >
                Cancel Order
              </Button>
              <Button
                variant="success"
                onClick={() => handleStatusChange('COMPLETED')}
              >
                Mark as Completed
              </Button>
            </div>
          </Offcanvas.Body>
        </Offcanvas>
      )}
    </div>
  );
};

export default RecentOrders;
