import React from 'react';
import SearchDropdown from './SearchDropdown'; // Import SearchDropdown component

const CategoryFilter = ({ selectedCategory, onCategoryChange, onSearch, onFilter }) => {
  const categories = ['All', 'Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Misc'];

  return (
    <div
      className="category-filter sticky-top border"
      style={{
        backgroundColor: '#1E1E1E', // Dark background color
        padding: '0.3rem 0.8rem', // Decreased padding for a smaller row
        display: 'flex',
        alignItems: 'center',
        gap: '0.8rem',
        position: 'sticky',
        top: '0',
        zIndex: '1200',
        height: '45px', // Reduced height
      }}
    >
      {/* Search Dropdown */}
      <SearchDropdown
        onSearch={onSearch}
        onFilter={onFilter}
        style={{ color: '#FFFFFF' }} // Ensure light/white icon color
      />

      {/* Category Links */}
      <div
        style={{
          display: 'flex',
          gap: '0.8rem',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          flexGrow: 1,
        }}
      >
        {categories.map((category) => (
          <span
            key={category}
            onClick={() => onCategoryChange(category)}
            style={{
              cursor: 'pointer',
              fontSize: '0.85rem', // Decreased font size
              fontWeight: selectedCategory === category ? 'bold' : 'normal',
              color: selectedCategory === category ? '#FFFFFF' : '#BBBBBB', // Light/white active and gray inactive text
              textTransform: 'uppercase',
              borderBottom: selectedCategory === category ? '2px solid #FFFFFF' : 'none', // Light/white underline for active
              paddingBottom: '0.2rem', // Reduced padding for compactness
              transition: 'color 0.3s ease, border-bottom 0.3s ease',
            }}
          >
            {category}
          </span>
        ))}
      </div>
    </div>
  );
};

export default CategoryFilter;
