// src/components/Login.js
import React, { useState, useContext } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import { AuthContext } from '../context/AuthContext'; // Import AuthContext
import { useNavigate } from 'react-router-dom';
import api from '../api/api'; // Assuming api.js is used for Axios configuration

const Login = () => {
    const { setAuth } = useContext(AuthContext); // Access AuthContext to manage login state
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // Send login request to the backend
            const response = await api.post('/auth/login', { email, password });

            // Log the response data to check its structure and contents
            console.log("Response Data:", response.data); // Check this for debugging

            if (response.data.success && response.data.token) {
                localStorage.setItem('token', response.data.token); // Save token to localStorage
                setAuth({ token: response.data.token }); // Set auth context
                setAuth({
                    token: response.data.token,
                    vendorId: response.data.vendorId, // Include vendorId
                });
                navigate('/test-dashboard'); // Redirect to the vendor dashboard
            } else {
                setError('Login failed. Please try again.');
            }
        } catch (error) {
            console.log("Error:", error.response?.data); // Log error details if any
            setError(error.response?.data?.message || 'An error occurred during login.');
        }
    };

    return (
        <div className="container mt-5" style={{ maxWidth: '400px' }}>
            <Card className="shadow-sm p-4">
                <Card.Body>
                    <h3 className="text-center mb-4">Vendor Sign In</h3>
                    {error && <Alert variant="danger">{error}</Alert>}

                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="email" className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                placeholder="Enter your email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="password" className="mb-3">
                            <Form.Label>Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Enter your password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <div className="d-flex justify-content-between align-items-center mt-4">
                            <Button variant="primary" type="submit" className="w-50">
                                Sign In
                            </Button>
                            <a href="/register" className="text-primary ms-3">Register</a>
                        </div>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    );
};

export default Login;
