import React, { useState } from 'react';
import { Dropdown, Form, Button } from 'react-bootstrap';

const SearchDropdown = ({ onSearch, onFilter }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    sativa: false,
    hybrid: false,
    indica: false,
  });

  const handleCheckboxChange = (classification) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [classification]: !prevFilters[classification],
    }));
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const handleApplyFilters = () => {
    onFilter(filters);
  };

  return (
    <Dropdown>
      <Dropdown.Toggle variant="outline-primary">
        <i className="bi bi-search"></i> Filter
      </Dropdown.Toggle>
      <Dropdown.Menu className="p-3" style={{ minWidth: '300px' }}>
        <Form.Group className="mb-3">
          <Form.Control
            type="text"
            placeholder="Search by brand or product name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </Form.Group>
        <Form>
          <Form.Check
            type="checkbox"
            label="Sativa"
            checked={filters.sativa}
            onChange={() => handleCheckboxChange('sativa')}
          />
          <Form.Check
            type="checkbox"
            label="Hybrid"
            checked={filters.hybrid}
            onChange={() => handleCheckboxChange('hybrid')}
          />
          <Form.Check
            type="checkbox"
            label="Indica"
            checked={filters.indica}
            onChange={() => handleCheckboxChange('indica')}
          />
        </Form>
        <Button
          variant="primary"
          className="mt-3 w-100"
          onClick={handleApplyFilters}
        >
          Apply Filters
        </Button>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default SearchDropdown;
