import React, { useState, useEffect } from 'react';
import { Dropdown, Button } from 'react-bootstrap';

const VendorStatusDropdown = ({ vendor }) => {
  const [status, setStatus] = useState('Closed');

  // Get current status based on business hours
  useEffect(() => {
    const checkStatus = () => {
      const today = new Date();
      const currentDay = today.toLocaleString('en-US', { weekday: 'long' }).toLowerCase(); // e.g., "monday"
      const currentTime = today.toTimeString().split(' ')[0]; // "HH:MM:SS"

      if (
        vendor.businessHours[currentDay] &&
        vendor.businessHours[currentDay].open &&
        vendor.businessHours[currentDay].close
      ) {
        const { open, close } = vendor.businessHours[currentDay];
        setStatus(currentTime >= open && currentTime <= close ? 'Available' : 'Closed');
      } else {
        setStatus('Closed');
      }
    };

    checkStatus();
    const interval = setInterval(checkStatus, 60000); // Check every minute
    return () => clearInterval(interval);
  }, [vendor.businessHours]);

  return (
    <Dropdown>
      <Dropdown.Toggle
        variant={status === 'Available' ? 'success' : 'danger'}
        id="vendor-status-dropdown"
        className="mb-3"
      >
        {status}
      </Dropdown.Toggle>

      <Dropdown.Menu>
        {/* Call Vendor */}
        {vendor.contactNumber && (
          <Dropdown.Item
            href={`tel:${vendor.contactNumber}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            Call Vendor
          </Dropdown.Item>
        )}

        {/* Text Vendor */}
        {vendor.contactNumber && (
          <Dropdown.Item
            href={`sms:${vendor.contactNumber}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            Text Vendor
          </Dropdown.Item>
        )}

        {/* Divider */}
        <Dropdown.Divider />

        {/* Weekly Business Hours */}
        <div className="px-3">
          <strong>Business Hours:</strong>
          <ul className="list-unstyled mb-0">
            {Object.entries(vendor.businessHours).map(([day, hours]) => (
              <li key={day}>
                {day.charAt(0).toUpperCase() + day.slice(1)}:{' '}
                {hours.open && hours.close ? `${hours.open} - ${hours.close}` : 'Closed'}
              </li>
            ))}
          </ul>
        </div>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default VendorStatusDropdown;
