import React, { useState } from "react";
import { Modal, Form, Button, Alert } from "react-bootstrap";
import axios from "axios";

const VendorRegistration = ({ show, handleClose }) => {
  const [step, setStep] = useState(1); // Step tracker
  const [formData, setFormData] = useState({
    licenseState: "",
    licenseNumber: "",
    dispensaryType: "",
    businessAddress: {
      street: "",
      city: "",
      state: "",
      zip: "",
    },
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    agreeToTerms: false,
  });
  const [showValidation, setShowValidation] = useState(false); // Validation toggle
  const [error, setError] = useState(null); // Backend error handling
  const [success, setSuccess] = useState(false); // Registration success message

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  const handleNextStep = () => setStep((prev) => prev + 1);
  const handlePrevStep = () => setStep((prev) => prev - 1);



  const handleSubmit = async () => {
    try {
      setError(null); // Reset error state
      const response = await axios.post("/api/auth/register", formData);

      if (response.data.success) {
        setSuccess(true);
        setTimeout(() => {
          handleClose(); // Close modal after showing success message
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor. Please try again.");
    }
  };

  return (
    <Modal show={show} centered onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{`Registration ${step}/3`}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {error && <Alert variant="danger">{error}</Alert>}
        {success && <Alert variant="success">Vendor registered successfully!</Alert>}

        {/* Step 1: Dispensary Info */}
        {step === 1 && (
          <>
            <h5>Dispensary Details</h5>
            <Form.Group className="mb-3">
              <Form.Label>License Info</Form.Label>
              <div className="d-flex">
                <Form.Select
                  name="licenseState"
                  value={formData.licenseState}
                  onChange={handleChange}
                  required
                  style={{ width: "120px" }}
                >
                  <option value="">State</option>
                  <option value="CA">AZ</option>
                  <option value="CA">CA</option>
                  <option value="CO">CO</option>
                  <option value="NV">MI</option>
                  <option value="NV">NJ</option>
                  <option value="NV">NY</option>
                  <option value="NV">OR</option>
                  {/* Add more states */}
                </Form.Select>
                <Form.Control
                  type="text"
                  placeholder="License Number"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleChange}
                  required
                  className="ms-3"
                />
              </div>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>What service do you provide?</Form.Label>
              <Form.Select
                name="dispensaryType"
                value={formData.dispensaryType}
                onChange={handleChange}
                required
              >
                <option value="">Select a Service</option>
                <option value="pickup">Pickup</option>
                <option value="delivery">Delivery</option>
                <option value="both">Pickup & Delivery</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
             <Form.Label>Dispensary Name</Form.Label>
             <Form.Control
              type="text"
              placeholder="Enter your dispensary name"
              name="dispensaryName"
              value={formData.dispensaryName}
              onChange={handleChange}
              required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Business Address</Form.Label>
              <Form.Control
                type="text"
                placeholder="Street"
                value={formData.businessAddress.street}
                onChange={(e) =>
                  handleNestedChange("businessAddress", "street", e.target.value)
                }
              />
              <Form.Control
                type="text"
                placeholder="City"
                className="mt-2"
                value={formData.businessAddress.city}
                onChange={(e) =>
                  handleNestedChange("businessAddress", "city", e.target.value)
                }
              />
              <Form.Control
                type="text"
                placeholder="State"
                className="mt-2"
                value={formData.businessAddress.state}
                onChange={(e) =>
                  handleNestedChange("businessAddress", "state", e.target.value)
                }
              />
              <Form.Control
                type="text"
                placeholder="Zip Code"
                className="mt-2"
                value={formData.businessAddress.zip}
                onChange={(e) =>
                  handleNestedChange("businessAddress", "zip", e.target.value)
                }
              />
            </Form.Group>

            {showValidation && (
              <div className="text-danger">
                {!formData.licenseState && <p>License State is required.</p>}
                {!formData.licenseNumber && <p>License Number is required.</p>}
                {!formData.dispensaryType && <p>Dispensary Type is required.</p>}
                {!formData.dispensaryName && <p>Dispensary Name is required.</p>}
                {(!formData.businessAddress.street ||
                  !formData.businessAddress.city ||
                  !formData.businessAddress.state ||
                  !formData.businessAddress.zip) && <p>Complete Business Address is required.</p>}
              </div>
            )}
          </>
        )}

        {/* Step 2: Business Owner Info */}
        {step === 2 && (
          <>
            <h5>Business Owner Details</h5>
            <div className="d-flex justify-content-between mb-3">
              <Form.Group className="me-2 flex-grow-1">
                <Form.Label>First Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your first name"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group className="flex-grow-1">
                <Form.Label>Last Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your last name"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                />
              </Form.Group>
            </div>

            <Form.Group className="mb-3">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="tel"
                placeholder="Enter your phone number"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Create a password"
                name="password"
                value={formData.password}
                onChange={handleChange}
              />
            </Form.Group>

            {showValidation && (
              <div className="text-danger">
                {!formData.firstName && <p>First Name is required.</p>}
                {!formData.lastName && <p>Last Name is required.</p>}
                {!/^\d{10}$/.test(formData.contactNumber) && (
                  <p>Enter a valid 10-digit phone number.</p>
                )}
                {!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email) && (
                  <p>Enter a valid email address.</p>
                )}
                {formData.password.length < 8 && (
                  <p>Password must be at least 8 characters long.</p>
                )}
              </div>
            )}
          </>
        )}

        {/* Step 3: Terms & Conditions */}
        {step === 3 && (
          <>
            <h5>Vendor Terms & Conditions</h5>
            <div
              className="border p-3 mb-3"
              style={{
                height: "200px",
                overflowY: "scroll",
                backgroundColor: "#f8f9fa",
                borderRadius: "5px",
              }}
            >
              <p>Terms and Conditions content goes here...</p>
            </div>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                id="agreeToTerms"
                label="I have read, understand, and agree to the terms and conditions."
                checked={formData.agreeToTerms}
                onChange={handleChange}
                name="agreeToTerms"
              />
            </Form.Group>
          </>
        )}
      </Modal.Body>
      <Modal.Footer>
        {step > 1 && (
          <Button variant="secondary" onClick={handlePrevStep}>
            Back
          </Button>
        )}
        {step < 3 && (
          <Button
            variant="primary"
            onClick={() => {
              const incomplete =
                (step === 1 &&
                  (!formData.licenseState ||
                    !formData.licenseNumber ||
                    !formData.dispensaryType ||
                    !formData.businessAddress.street ||
                    !formData.businessAddress.city ||
                    !formData.businessAddress.state ||
                    !formData.businessAddress.zip)) ||
                (step === 2 &&
                  (!formData.firstName ||
                    !formData.lastName ||
                    !/^\d{10}$/.test(formData.contactNumber) ||
                    !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email) ||
                    formData.password.length < 8));
              if (incomplete) {
                setShowValidation(true);
              } else {
                setShowValidation(false);
                handleNextStep();
              }
            }}
          >
            Next
          </Button>
        )}
        {step === 3 && (
          <Button
            variant="success"
            onClick={handleSubmit}
            disabled={!formData.agreeToTerms}
          >
            Register
          </Button>
        )}
      </Modal.Footer>
    </Modal>
  );
};

export default VendorRegistration;
