import React, { useState } from 'react';
import { Modal, Form, Alert, Button, Row, Col } from 'react-bootstrap';
import { X, Plus, Image as ImageIcon } from 'lucide-react';

const CreateProductModal = ({ show, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    brand: '',
    name: '',
    category: 'Flower',
    description: '',
    variations: [
      {
        amount: '',
        strain: '',
        thcContent: '',
        price: '',
        salePrice: '',
        stock: '',
        image: null
      }
    ]
  });

  const [errors, setErrors] = useState({});
  const [imageUrls, setImageUrls] = useState([]);

  const categories = [
    'Flower',
    'Pre-roll',
    'Vape',
    'Edible',
    'Concentrate',
    'Tincture',
    'Gear'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleVariationChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      variations: prev.variations.map((v, i) => 
        i === index ? { ...v, [field]: value } : v
      )
    }));
    if (errors[`variation-${index}-${field}`]) {
      setErrors(prev => ({
        ...prev,
        [`variation-${index}-${field}`]: ''
      }));
    }
  };

  const handleImageUpload = (index, e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file size
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        alert('File size too large. Please choose an image under 5MB.');
        return;
      }
      // Validate file type
      if (!file.type.startsWith('image/')) {
        alert('Please upload only image files.');
        return;
      }
      const url = URL.createObjectURL(file);
      setImageUrls(prev => {
        const newUrls = [...prev];
        newUrls[index] = url;
        return newUrls;
      });
      handleVariationChange(index, 'image', file);
    }
  };

  const addVariation = () => {
    setFormData(prev => ({
      ...prev,
      variations: [...prev.variations, {
        amount: '',
        strain: '',
        thcContent: '',
        price: '',
        salePrice: '',
        stock: '',
        image: null
      }]
    }));
  };

  const removeVariation = (index) => {
    setFormData(prev => ({
      ...prev,
      variations: prev.variations.filter((_, i) => i !== index)
    }));
    setImageUrls(prev => prev.filter((_, i) => i !== index));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.brand.trim()) newErrors.brand = 'Brand is required';
    if (!formData.name.trim()) newErrors.name = 'Product name is required';
    if (!formData.category) newErrors.category = 'Category is required';

    formData.variations.forEach((variation, index) => {
      if (!variation.amount && formData.category !== 'Gear') {
        newErrors[`variation-${index}-amount`] = 'Amount is required';
      }
      if (!variation.strain && formData.category !== 'Gear') {
        newErrors[`variation-${index}-strain`] = 'Strain is required';
      }
      if (!variation.price || variation.price <= 0) {
        newErrors[`variation-${index}-price`] = 'Valid price is required';
      }
      if (variation.salePrice && Number(variation.salePrice) >= Number(variation.price)) {
        newErrors[`variation-${index}-salePrice`] = 'Sale price must be less than regular price';
      }
      if (!variation.stock || variation.stock < 0) {
        newErrors[`variation-${index}-stock`] = 'Valid stock is required';
      }
      if (!variation.image) {
        newErrors[`variation-${index}-image`] = 'Image is required';
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      onSubmit(formData);
    }
  };

  return (
    <Modal show={show} onHide={onClose} size="xl">
      <Modal.Header closeButton>
        <Modal.Title className="fw-bold">Add New Product</Modal.Title>
      </Modal.Header>

      <Modal.Body className="max-h-[80vh] overflow-y-auto">
        <Form onSubmit={handleSubmit}>
          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Brand</Form.Label>
                <Form.Control
                  type="text"
                  name="brand"
                  value={formData.brand}
                  onChange={handleInputChange}
                  isInvalid={!!errors.brand}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.brand}
                </Form.Control.Feedback>
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Product Name</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  isInvalid={!!errors.name}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.name}
                </Form.Control.Feedback>
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Category</Form.Label>
                <Form.Select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                >
                  {categories.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>

            <Col md={12}>
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Description</Form.Label>
                <Form.Control
                  as="textarea"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  rows={3}
                />
              </Form.Group>
            </Col>
          </Row>

          <div className="border-top pt-4">
            <h4 className="mb-4">Product Variations</h4>
            {formData.variations.map((variation, index) => (
              <div
                key={index}
                className="mb-4 p-4 rounded"
                style={{ backgroundColor: '#f8f9fa' }}
              >
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h5 className="mb-0">Variation #{index + 1}</h5>
                  {index > 0 && (
                    <Button
                      variant="link"
                      className="text-danger p-0"
                      onClick={() => removeVariation(index)}
                    >
                      <X />
                    </Button>
                  )}
                </div>

                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Amount</Form.Label>
                      <Form.Control
                        type="text"
                        value={variation.amount}
                        onChange={(e) => handleVariationChange(index, 'amount', e.target.value)}
                        isInvalid={!!errors[`variation-${index}-amount`]}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors[`variation-${index}-amount`]}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>

                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Strain</Form.Label>
                      <Form.Control
                        type="text"
                        value={variation.strain}
                        onChange={(e) => handleVariationChange(index, 'strain', e.target.value)}
                        isInvalid={!!errors[`variation-${index}-strain`]}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors[`variation-${index}-strain`]}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">THC Content (%)</Form.Label>
                      <Form.Control
                        type="number"
                        step="0.1"
                        value={variation.thcContent}
                        onChange={(e) => handleVariationChange(index, 'thcContent', e.target.value)}
                      />
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Price ($)</Form.Label>
                      <Form.Control
                        type="number"
                        step="0.01"
                        value={variation.price}
                        onChange={(e) => handleVariationChange(index, 'price', e.target.value)}
                        isInvalid={!!errors[`variation-${index}-price`]}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors[`variation-${index}-price`]}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Sale Price ($)</Form.Label>
                      <Form.Control
                        type="number"
                        step="0.01"
                        value={variation.salePrice}
                        onChange={(e) => handleVariationChange(index, 'salePrice', e.target.value)}
                        isInvalid={!!errors[`variation-${index}-salePrice`]}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors[`variation-${index}-salePrice`]}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Stock</Form.Label>
                      <Form.Control
                        type="number"
                        value={variation.stock}
                        onChange={(e) => handleVariationChange(index, 'stock', e.target.value)}
                        isInvalid={!!errors[`variation-${index}-stock`]}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors[`variation-${index}-stock`]}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>

                  <Col md={12}>
                    <Form.Group className="mb-3">
                      <Form.Label className="fw-semibold">Image</Form.Label>
                      <div className="d-flex align-items-center gap-3">
                        {imageUrls[index] ? (
                          <div className="position-relative">
                            <img
                              src={imageUrls[index]}
                              alt={`Variation ${index + 1}`}
                              className="rounded"
                              style={{ width: '120px', height: '120px', objectFit: 'cover' }}
                            />
                            <Button
                              variant="danger"
                              size="sm"
                              className="position-absolute top-0 end-0 m-1 p-1"
                              onClick={() => {
                                setImageUrls(prev => {
                                  const newUrls = [...prev];
                                  newUrls[index] = null;
                                  return newUrls;
                                });
                                handleVariationChange(index, 'image', null);
                              }}
                            >
                              <X size={14} />
                            </Button>
                          </div>
                        ) : (
                          <div>
                            <input
                              type="file"
                              accept="image/*"
                              onChange={(e) => handleImageUpload(index, e)}
                              className="d-none"
                              id={`image-upload-${index}`}
                            />
                            <label
                              htmlFor={`image-upload-${index}`}
                              className="d-flex flex-column align-items-center justify-content-center p-4 border border-2 border-dashed rounded cursor-pointer"
                              style={{ width: '120px', height: '120px', cursor: 'pointer' }}
                            >
                              <ImageIcon className="mb-2" />
                              <span className="small text-muted">Upload Image</span>
                            </label>
                          </div>
                        )}
                        {errors[`variation-${index}-image`] && (
                          <div className="text-danger small">
                            {errors[`variation-${index}-image`]}
                          </div>
                        )}
                      </div>
                    </Form.Group>
                  </Col>
                </Row>
              </div>
            ))}

            <Button
              variant="secondary"
              className="w-100 mb-4"
              onClick={addVariation}
              style={{ height: '100px' }}
            >
              <Plus className="me-2" />
              Add Another Variation
            </Button>
          </div>

          {Object.keys(errors).length > 0 && (
            <Alert variant="danger" className="mt-3">
              Please fix the errors above before submitting.
            </Alert>
          )}
        </Form>
      </Modal.Body>

      <Modal.Footer className="border-top">
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Create Product
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateProductModal;
