import React, { useEffect } from 'react';

const MenuPickupDeliveryToggle = ({ vendor, orderType, setOrderType }) => {
  const isDeliveryAvailable =
    vendor.dispensaryType === 'Delivery' || vendor.dispensaryType === 'Pickup & Delivery';
  const isPickupAvailable =
    vendor.dispensaryType === 'Pickup' || vendor.dispensaryType === 'Pickup & Delivery';

  // Set initial toggle state based on vendor's dispensaryType
  useEffect(() => {
    if (isDeliveryAvailable) {
      setOrderType('Delivery');
    } else if (isPickupAvailable) {
      setOrderType('Pickup');
    }
  }, [isDeliveryAvailable, isPickupAvailable, setOrderType]);

  return (
    <div className="btn-group" role="group" aria-label="Delivery or Pickup toggle">
      {/* Display Delivery Button only if available */}
      {isDeliveryAvailable && (
        <>
          <input
            type="radio"
            className="btn-check"
            name="btnradio"
            id="btnradio1"
            autoComplete="off"
            checked={orderType === 'Delivery'}
            onChange={() => setOrderType('Delivery')}
          />
          <label
            className={`btn ${orderType === 'Delivery' ? 'text-white' : 'text-secondary'}`}
            style={{
              backgroundColor: orderType === 'Delivery' ? '#1E2024' : 'white',
              border: '1px solid black',
              padding: '4px 12px',
              fontSize: '14px',
              fontFamily: 'Poppins, sans-serif',
              fontWeight: orderType === 'Delivery' ? '600' : '400',
            }}
            htmlFor="btnradio1"
          >
            Delivery
          </label>
        </>
      )}

      {/* Display Pickup Button only if available */}
      {isPickupAvailable && (
        <>
          <input
            type="radio"
            className="btn-check"
            name="btnradio"
            id="btnradio2"
            autoComplete="off"
            checked={orderType === 'Pickup'}
            onChange={() => setOrderType('Pickup')}
          />
          <label
            className={`btn ${orderType === 'PICKUP' ? 'text-white' : 'text-secondary'}`}
            style={{
              backgroundColor: orderType === 'PICKUP' ? '#1E2024' : 'white',
              border: '1px solid black',
              padding: '4px 12px',
              fontSize: '14px',
              fontFamily: 'Poppins, sans-serif',
              fontWeight: orderType === 'PICKUP' ? '600' : '400',
            }}
            htmlFor="btnradio2"
          >
            Pickup
          </label>
        </>
      )}
    </div>
  );
};

export default MenuPickupDeliveryToggle;
