import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext'; // Import useAuth hook
import MapComponent from './MapComponent';

function UpdateDeliveryZoneComponent({ isLoaded, loadError }) {
  const { auth } = useAuth(); // Use auth context for authentication state
  const [deliveryZone, setDeliveryZone] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch delivery zone on component mount
  useEffect(() => {
    const fetchDeliveryZone = async () => {
      if (!auth?.vendorId) {
        setError('You must be logged in to set your delivery zone.');
        return;
      }

      try {
        const response = await fetch(`/api/vendors/delivery-zone`, {
          method: 'GET',
          credentials: 'include', // Include JWT cookie
        });

        if (!response.ok) {
          console.error('Failed to fetch delivery zone:', response.statusText);
          setError('Failed to load delivery zone. Please try again later.');
          return;
        }

        const result = await response.json();
        if (result.deliveryZone?.coordinates) {
          const [lng, lat] = result.deliveryZone.coordinates; // GeoJSON format
          setDeliveryZone({ lat, lng });
          setError(''); // Clear previous errors
        }
      } catch (error) {
        console.error('Error fetching delivery zone:', error);
        setError('An error occurred while fetching delivery zone.');
      }
    };

    fetchDeliveryZone();
  }, [auth?.vendorId]); // Refetch if auth.vendorId changes

  const handleUpdateDeliveryZone = async () => {
    if (!deliveryZone || !deliveryZone.lat || !deliveryZone.lng) {
      alert('Please select a valid delivery zone before updating.');
      return;
    }

    try {
      const geoJsonDeliveryZone = {
        type: 'Point',
        coordinates: [deliveryZone.lng, deliveryZone.lat], // GeoJSON format
      };

      const response = await fetch(`/api/vendors/delivery-zone`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Include JWT cookie
        body: JSON.stringify({ deliveryZone: geoJsonDeliveryZone }),
      });

      if (response.ok) {
        setSuccess('Delivery zone updated successfully!');
        setError('');
      } else {
        setError('Failed to update delivery zone. Please try again.');
      }
    } catch (error) {
      console.error('Error updating delivery zone:', error);
      setError('An error occurred while updating delivery zone.');
    }
  };

  if (loadError) return <Alert variant="danger">Error loading map</Alert>;
  if (!isLoaded) return <Alert variant="info">Loading map...</Alert>;

  return (
    <Form className="p-4 border rounded shadow-sm">
      <h5 className="mb-3">Delivery Zone</h5>
      <p className="text-muted">
        Orders will be limited to within a 20-mile radius of the marker.
      </p>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <div className="mb-3 rounded border overflow-hidden">
        <MapComponent
          onLocationChange={setDeliveryZone}
          initialLocation={deliveryZone}
        />
      </div>
      <div className="d-flex justify-content-center">
        <Button variant="primary" onClick={handleUpdateDeliveryZone} className="mt-3 px-4 py-2">
          Update Delivery Zone
        </Button>
      </div>
    </Form>
  );
}

export default UpdateDeliveryZoneComponent;
