import React, { useEffect, useState, useContext } from 'react';
import { ListGroup, Badge, Button, Offcanvas } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const TestDashboard = () => {
  const { auth } = useContext(AuthContext); // Retrieve token and vendorId from context
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);
  const [copiedField, setCopiedField] = useState(null); // Track copied field

  useEffect(() => {
    const fetchOrders = async () => {
      if (!auth.vendorId) {
        console.error('vendorId is not defined in AuthContext');
        return;
      }

      try {
        const response = await axios.get(`/api/orders/vendor/${auth.vendorId}`, {
          headers: { Authorization: `Bearer ${auth.token}` }, // Pass token if route is protected
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching vendor orders:', error);
      }
    };

    fetchOrders();
  }, [auth.vendorId, auth.token]);

  const handleRowClick = (order) => {
    setSelectedOrder(order);
    setShowSlideIn(true);
  };

  const handleStatusChange = async (status) => {
    if (selectedOrder) {
      try {
        await axios.put(`/api/orders/${selectedOrder._id}/status`, { status }, {
          headers: { Authorization: `Bearer ${auth.token}` },
        });
        setOrders((prevOrders) =>
          prevOrders.map((order) =>
            order._id === selectedOrder._id ? { ...order, status } : order
          )
        );
        setShowSlideIn(false);
      } catch (error) {
        console.error('Error updating order status:', error);
      }
    }
  };

  const handleCopy = (text, field) => {
    navigator.clipboard.writeText(text);
    setCopiedField(field);
    setTimeout(() => setCopiedField(null), 2000);
  };

  return (
    <div>
      <h2 className="mb-4">Recent Orders</h2>
      <ListGroup>
        {orders.map((order) => (
          <ListGroup.Item
            key={order._id}
            className="d-flex justify-content-between align-items-center"
            style={{ cursor: 'pointer' }}
            onClick={() => handleRowClick(order)}
          >
            <div>
              <div className="text-muted">{new Date(order.createdAt).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}</div>
              <div className="fw-bold">
                {order.contactInfo?.firstName && order.contactInfo?.lastName
                  ? `${order.contactInfo.firstName} ${order.contactInfo.lastName}`
                  : 'Customer Name'}
              </div>
              <Badge bg={order.status === 'COMPLETED' ? 'success' : order.status === 'CANCELLED' ? 'danger' : 'warning'}>
                {order.status.replace('_', ' ')}
              </Badge>
            </div>
            <div>
              <span className="fw-bold">${order.total?.toFixed(2) || '0.00'}</span>
              <span className="ms-2">&gt;</span>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Slide-in Details Page */}
      {selectedOrder && (
        <Offcanvas show={showSlideIn} onHide={() => setShowSlideIn(false)} placement="end">
          <Offcanvas.Header>
            <Button
              variant="link"
              onClick={() => setShowSlideIn(false)}
              className="p-0 text-decoration-none"
            >
              <i className="bi bi-chevron-left fs-3"></i>
            </Button>
            <Offcanvas.Title className="mx-auto">Order Details</Offcanvas.Title>
          </Offcanvas.Header>
          <hr /> {/* Division line */}
          <Offcanvas.Body>
            <div className="text-muted mb-3" style={{ fontSize: '0.9rem' }}>
              {new Date(selectedOrder.createdAt).toLocaleString()}
            </div>
            <div className="mb-3 fw-bold" style={{ fontSize: '1.2rem' }}>
              {selectedOrder.contactInfo?.firstName && selectedOrder.contactInfo?.lastName
                ? `${selectedOrder.contactInfo.firstName} ${selectedOrder.contactInfo.lastName}`
                : 'Customer Name'}
            </div>
            <div className="mb-3 d-flex align-items-center">
              <Button
                variant="light"
                className="me-2"
                style={{ backgroundColor: '#f8f9fa' }}
              >
                <i className="bi bi-geo-alt-fill text-primary"></i>
              </Button>
              <div>
                <div>{selectedOrder.deliveryAddress?.street || 'N/A'}</div>
                <div>
                  {selectedOrder.deliveryAddress?.city}, {selectedOrder.deliveryAddress?.state || 'N/A'}
                </div>
                <div>{selectedOrder.deliveryAddress?.zip || 'N/A'}</div>
              </div>
              <Button
                variant="link"
                className="ms-2 p-0 text-muted"
                onClick={() =>
                  handleCopy(
                    `${selectedOrder.deliveryAddress?.street}, ${selectedOrder.deliveryAddress?.city}, ${selectedOrder.deliveryAddress?.state}, ${selectedOrder.deliveryAddress?.zip}`,
                    'address'
                  )
                }
              >
                {copiedField === 'address' ? 'Copied' : 'Copy'}
              </Button>
            </div>
            <div className="mb-3 d-flex align-items-center">
              <Button
                variant="light"
                className="me-2"
                style={{ backgroundColor: '#f8f9fa' }}
              >
                <i className="bi bi-telephone-fill text-success"></i>
              </Button>
              <span>{selectedOrder.contactInfo?.phone || 'N/A'}</span>
              <Button
                variant="link"
                className="ms-2 p-0 text-muted"
                onClick={() => handleCopy(selectedOrder.contactInfo?.phone || '', 'phone')}
              >
                {copiedField === 'phone' ? 'Copied' : 'Copy'}
              </Button>
            </div>
            <h5>Items</h5>
            <ListGroup>
              {selectedOrder.items.map((item, index) => (
                <ListGroup.Item key={index} className="d-flex align-items-center">
                  <img
                    src={item.thumbnail || 'https://via.placeholder.com/50'}
                    alt={item.name}
                    className="me-3"
                    style={{ width: '50px', height: '50px' }}
                  />
                  <div className="flex-grow-1">
                    <div className="fw-bold">{item.name}</div>
                    <div className="text-muted">${item.price?.toFixed(2) || 'N/A'} x {item.quantity}</div>
                  </div>
                </ListGroup.Item>
              ))}
            </ListGroup>
            <div className="d-flex justify-content-between mt-4">
              <Button variant="danger" onClick={() => handleStatusChange('CANCELLED')}>
                Cancel Order
              </Button>
              <Button variant="success" onClick={() => handleStatusChange('COMPLETED')}>
                Mark as Completed
              </Button>
            </div>
          </Offcanvas.Body>
        </Offcanvas>
      )}
    </div>
  );
};

export default TestDashboard;
