import React, { useEffect, useRef } from 'react';

const PickupMap = ({ dispensaries }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markers = useRef([]);

  useEffect(() => {
    if (!window.google) {
      console.error('Google Maps JavaScript API is not loaded.');
      return;
    }

    // Initialize map
    if (mapRef.current) {
      mapInstance.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: 34.0522, lng: -118.2437 }, // Default center (Los Angeles)
        zoom: 12,
      });
    }
  }, []);

  useEffect(() => {
    if (!mapInstance.current || !dispensaries.length) return;

    // Clear previous markers
    markers.current.forEach((marker) => marker.setMap(null));
    markers.current = [];

    // Add new markers
    dispensaries.forEach((dispensary) => {
      if (!dispensary.deliveryZone?.coordinates) return;

      const [lng, lat] = dispensary.deliveryZone.coordinates;

      const marker = new window.google.maps.Marker({
        position: { lat, lng },
        map: mapInstance.current,
        title: dispensary.dispensaryName,
      });

      const infoWindow = new window.google.maps.InfoWindow({
        content: `<div>
          <h6>${dispensary.dispensaryName}</h6>
          <p>${dispensary.businessAddress?.street || 'Address not available'}</p>
        </div>`
      });

      marker.addListener('click', () => {
        infoWindow.open({
          anchor: marker,
          map: mapInstance.current,
          shouldFocus: false,
        });
      });

      markers.current.push(marker);
    });
  }, [dispensaries]);

  return <div ref={mapRef} style={{ width: '100%', height: '500px' }}></div>;
};

export default PickupMap;
