import React, { useState, useEffect } from 'react';
import { Offcanvas, ListGroup, Button, Form, InputGroup, FormControl, Badge, Modal } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import AddProductPage from './AddProductPage';

const InventoryComponent = () => {
  const { auth } = useAuth();
  const [inventory, setInventory] = useState({});
  const [filteredInventory, setFilteredInventory] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const formatter = new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' });

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const response = await axios.get(`/api/vendor/inventory/${auth.vendorId}`);
        const groupedData = response.data.reduce((acc, item) => {
          const category = item.productId.category || 'Uncategorized';
          if (!acc[category]) acc[category] = [];
          acc[category].push(item);
          return acc;
        }, {});
        setInventory(groupedData);
        setFilteredInventory(groupedData);
      } catch (error) {
        console.error('Error fetching inventory:', error);
      }
    };

    fetchInventory();
  }, [auth.vendorId]);

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    if (!term) {
      setFilteredInventory(inventory);
      return;
    }
    const filtered = {};
    Object.keys(inventory).forEach((category) => {
      const products = inventory[category].filter(
        (item) =>
          item.productId.name.toLowerCase().includes(term) ||
          item.productId.brand.toLowerCase().includes(term)
      );
      if (products.length) filtered[category] = products;
    });
    setFilteredInventory(filtered);
  };

  const handleRowClick = (product) => {
    setSelectedProduct(product);
    setShowSlideIn(true);
  };

  const handleStatusToggle = async () => {
    try {
      const newStatus = selectedProduct.status === 'Active' ? 'Disabled' : 'Active';
      await axios.patch(`/api/vendor/inventory/${selectedProduct._id}/status`, { status: newStatus });
      setInventory((prev) => {
        const updated = { ...prev };
        const category = selectedProduct.productId.category || 'Uncategorized';
        updated[category] = updated[category].map((item) =>
          item._id === selectedProduct._id ? { ...item, status: newStatus } : item
        );
        return updated;
      });
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/vendor/inventory/${selectedProduct._id}`);
      setInventory((prev) => {
        const updated = { ...prev };
        const category = selectedProduct.productId.category || 'Uncategorized';
        updated[category] = updated[category].filter((item) => item._id !== selectedProduct._id);
        return updated;
      });
      setShowDeleteModal(false);
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      {/* Search Bar and Add Button */}
      <div className="d-flex align-items-center justify-content-between mb-3">
        <InputGroup className="w-75">
          <FormControl
            placeholder="Search by brand or product name"
            value={searchTerm}
            onChange={handleSearch}
          />
        </InputGroup>
        <Button variant="success" onClick={() => setShowAddProduct(true)}>
          + Add Product
        </Button>
      </div>

      {/* Inventory List */}
      {Object.keys(filteredInventory).map((category) => (
        <div key={category}>
          <h4>{category}</h4>
          <ListGroup>
            {filteredInventory[category].map((product) => (
              <ListGroup.Item
                key={product._id}
                className="d-flex justify-content-between align-items-center"
                style={{ cursor: 'pointer' }}
                onClick={() => handleRowClick(product)}
              >
                <div>
                  <div className="fw-bold">{product.productId.brand}</div>
                  <div className="text-muted">{product.productId.name}</div>
                  <Badge bg={product.stock === 0 ? 'secondary' : product.status === 'Active' ? 'success' : 'secondary'}>
                    {product.stock === 0 ? 'Out of Stock' : product.status}
                  </Badge>
                </div>
                <span>{formatter.format(product.price)}</span>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </div>
      ))}

      {/* Slide-in Window */}
      {selectedProduct && (
        <Offcanvas show={showSlideIn} onHide={() => setShowSlideIn(false)} placement="end">
          <Offcanvas.Header closeButton>
            <Offcanvas.Title className="d-flex justify-content-between align-items-center w-100">
              <div>
                {selectedProduct.productId.brand} - {selectedProduct.productId.name}
              </div>
              <Button
                variant={selectedProduct.status === 'Active' ? 'secondary' : 'success'}
                onClick={handleStatusToggle}
              >
                {selectedProduct.status === 'Active' ? 'Disable' : 'Enable'}
              </Button>
            </Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Form>
              <Form.Group>
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter price"
                  value={selectedProduct.price}
                  readOnly
                />
              </Form.Group>
            </Form>
            <Button variant="danger" className="mt-3" onClick={() => setShowDeleteModal(true)}>
              Delete Product
            </Button>
          </Offcanvas.Body>
        </Offcanvas>
      )}

      {/* Confirm Delete Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Deletion</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete this product?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Add Product Slide-in */}
      <Offcanvas show={showAddProduct} onHide={() => setShowAddProduct(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Add Product</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <AddProductPage />
        </Offcanvas.Body>
      </Offcanvas>
    </div>
  );
};

export default InventoryComponent;
