import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const AddProductPage = () => {
  const { auth } = useAuth();
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedVariation, setSelectedVariation] = useState('');
  const [availableStrains, setAvailableStrains] = useState([]);
  const [price, setPrice] = useState('');
  const [salePrice, setSalePrice] = useState('');
  const [stock, setStock] = useState('');
  const [weight, setWeight] = useState('');
  const [thcContent, setThcContent] = useState('');
  const [selectedImage, setSelectedImage] = useState('');

  // Fetch products by brand and category
  useEffect(() => {
    if (brand) {
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setSelectedImage(product.images?.[0] || '');
    setSelectedVariation('');
    setAvailableStrains([]);
  };

  const handleVariationSelect = (amount) => {
    setSelectedVariation(amount);
    if (selectedProduct) {
      const strains = selectedProduct.variations
        .filter((variation) => variation.amount === amount)
        .map((variation) => variation.strain);
      setAvailableStrains(strains);
    }
  };

  const handleAddToInventory = async () => {
    if (!selectedProduct || !selectedVariation || !price || !stock) {
      alert('Please fill out all required fields.');
      return;
    }

    try {
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          price: parseFloat(price),
          salePrice: salePrice ? parseFloat(salePrice) : undefined,
          stock: parseInt(stock, 10),
          weight,
          thcContent: thcContent ? parseFloat(thcContent) : undefined,
          variation: {
            amount: selectedVariation,
            strain: availableStrains.length === 1 ? availableStrains[0] : undefined, // Select strain if only one is available
          },
          selectedImage,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Reset fields
        setBrand('');
        setCategory('Flower');
        setSelectedProduct(null);
        setSelectedVariation('');
        setAvailableStrains([]);
        setPrice('');
        setSalePrice('');
        setStock('');
        setWeight('');
        setThcContent('');
        setSelectedImage('');
      } else {
        alert(data.message || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };

  return (
    <div className="container mt-4">
      <h1>Add Product to Inventory</h1>
      <div className="mb-3">
        <label>Category</label>
        <select className="form-select" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Topical">Topical</option>
          <option value="Gear">Gear</option>
        </select>
      </div>

      <div className="mb-3">
        <label>Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {products.length > 0 && (
        <div className="mb-3">
          <label>Matching Products</label>
          <ul className="list-group">
            {products.map((product) => (
              <li
                key={product._id}
                className={`list-group-item ${selectedProduct?._id === product._id ? 'active' : ''}`}
                onClick={() => handleProductSelect(product)}
                style={{ cursor: 'pointer' }}
              >
                <strong>{product.brand}</strong> - {product.name} - {product.variations?.[0]?.amount}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedProduct && (
        <>
          <div className="mb-3">
            <label>Select Variation Amount</label>
            <select
              className="form-select"
              value={selectedVariation}
              onChange={(e) => handleVariationSelect(e.target.value)}
            >
              <option value="" disabled>
                Select amount
              </option>
              {selectedProduct.variations.map((variation, index) => (
                <option key={index} value={variation.amount}>
                  {variation.amount}
                </option>
              ))}
            </select>
          </div>

          {availableStrains.length > 0 && (
            <div className="mb-3">
              <label>Select Strain</label>
              <select className="form-select">
                {availableStrains.map((strain, index) => (
                  <option key={index} value={strain}>
                    {strain}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Add other input fields here as in your original code */}
        </>
      )}
    </div>
  );
};

export default AddProductPage;
