import React, { useContext } from 'react';
import { Card, Button } from 'react-bootstrap'; // Add this line to import Card and Button
import { CartContext } from '../context/CartContext';

const CartSummary = () => {
  const { cartItems, calculateTotal, incrementItem, decrementItem, removeFromCart } = useContext(CartContext); // Added removeFromCart

  return (
    <Card className="cart-summary-card mb-1" style={{ border: 'none', backgroundColor: '#f8f9fa', margin: '20px', borderRadius: '8px', }}> {/* Removed outer border */}
      <Card.Body>
        {/* Display Cart Items */}
        {cartItems.length > 0 ? (
          <>
            {cartItems.map((item) => (
              <div key={item.id} className="d-flex justify-content-between align-items-center mb-3">
                {/* Product Image and Name */}
                <div className="d-flex align-items-center">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="img-fluid"
                    style={{ width: '40px', height: '40px', marginRight: '10px' }} // Very small image
                  />
                  <div>
                    <div>{item.ame}</div> {/* Product name */}
                    <div>
                      ${item.price.toFixed(2)}{' '}
                      <a
                        href="#"
                        className="text-muted small"
                        onClick={(e) => {
                          e.preventDefault();
                          removeFromCart(item.id); // Remove item from cart
                        }}
                        style={{ textDecoration: 'underline', marginLeft: '5px' }}
                      >
                        remove
                      </a>
                    </div>
                  </div>
                </div>

                {/* Quantity and Controls */}
                <div className="d-flex align-items-center">
                  <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={() => decrementItem(item.id)}
                    style={{ marginRight: '5px' }}
                  >
                    -
                  </Button>
                  <span className="mx-2">{item.quantity}</span> {/* Current number in cart */}
                  <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={() => incrementItem(item.id)}
                    style={{ marginLeft: '5px' }}
                  >
                    +
                  </Button>
                </div>
              </div>
            ))}

            {/* Subtotal */}
            <div className="d-flex justify-content-between mt-3">
              <strong>Subtotal</strong>
              <span>${calculateTotal().toFixed(2)}</span>
            </div>
          </>
        ) : (
          <>
            {/* Message for Empty Cart */}
            <p className="text-center mb-1">Your cart is empty.</p>
          </>
        )}
      </Card.Body>
    </Card>
  );
};

export default CartSummary;
