import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import PickupDeliveryToggle from './PickupDeliveryToggle.js';
import MenuSearchBar from './MenuSearchBar.js';
import ProductRow from './ProductRow';
import VendorDetails from './VendorDetails.js';
import CategoryButtons from './CategoryButtons.js';
import AddressFilter from './AddressFilter.js';
import './VendorMenu.css';
import products from '../data/products'; // Import stock products

const VendorMenu = () => {
  const { id } = useParams(); // Get vendor ID from the URL
  const vendorId = parseInt(id, 10); // Convert the vendor ID to a number

  // State variables
  const [menuMode, setMenuMode] = useState('Delivery'); // Default to Delivery menu
  const [address, setAddress] = useState('123 Main St, Los Angeles, CA');
  const [filteredProducts, setFilteredProducts] = useState([]); // Filtered products based on vendor
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [vendorProducts, setVendorProducts] = useState([]); // Store vendor-specific products

  // Filter products for the selected vendor on component load or when vendorId changes
  useEffect(() => {
    const vendorSpecificProducts = products.filter(product =>
      product.vendors.includes(vendorId)
    );
    setVendorProducts(vendorSpecificProducts); // Set the products available for this vendor
    setFilteredProducts(vendorSpecificProducts); // Initially show all vendor's products
  }, [vendorId]);

  // Handle mode changes (Pickup/Delivery)
  const handleToggle = (newMode) => {
    setMenuMode(newMode);
    console.log(`Switched to ${newMode} mode`);
  };

  // Handle address updates
  const handleUpdateAddress = (newAddress) => {
    setAddress(newAddress);
    console.log(`New address: ${newAddress}`);
  };

  // Handle search queries from the search bar
  const handleSearch = (query) => {
    if (query === '') {
      setFilteredProducts(vendorProducts); // If the query is empty, show all vendor's products
    } else {
      const lowercasedQuery = query.toLowerCase();
      const filtered = vendorProducts.filter((product) =>
        product.productName.toLowerCase().includes(lowercasedQuery)
      );
      setFilteredProducts(filtered); // Update the filtered products based on the query
    }
  };

  // Handle category selection
  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    if (category === 'All') {
      setFilteredProducts(vendorProducts); // Show all products for this vendor
    } else {
      const filtered = vendorProducts.filter(
        (product) => product.categoryName === category
      );
      setFilteredProducts(filtered); // Show products that match the selected category for this vendor
    }
    console.log(`Selected Category: ${category}`);
  };

  return (
    <div className="container full-width-section">
      {/* Vendor details */}
      <div className="bg-white rounded full-width-section">
        <VendorDetails
          logo={require('../assets/images/highTide.jpg')}
          vendorType="Rec & Med"
          vendorName="High Tide"
          vendorLocation="Los Angeles, CA"
          rating={4.5}
          status="Available"
        />
      </div>

      <hr className="border-top border-secondary m-0" />

      {/* Filters and search functionality */}
      <div className="bg-light pull-up p-3 pt-4">
        <PickupDeliveryToggle onToggle={handleToggle} />
        <AddressFilter onUpdateAddress={handleUpdateAddress} address={address} />
        <MenuSearchBar onSearch={handleSearch} />
        <CategoryButtons onCategorySelect={handleCategorySelect} />

        <hr className="my-3 border-top border-secondary" />

        {/* Product Rows - Display filtered products */}
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <ProductRow
              key={product.id}
              id={product.id}
              image={product.image}
              categoryName={product.categoryName}
              productName={product.productName}
              productAmount={product.productAmount}
              brand={product.brand}
              thcLevel={product.thcLevel}
              price={product.price}
              salePrice={product.salePrice}
              onAddToCart={() => alert(`${product.productName} added to cart!`)}
            />
          ))
        ) : (
          <p>No products available for this vendor.</p>
        )}
      </div>
    </div>
  );
};

export default VendorMenu;
