import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuthState] = useState({
        token: null,
        vendorId: null,
        vendorName: null, // Added vendor name
    });

    // Load token, vendorId, and vendorName from localStorage on initial load
    useEffect(() => {
        const storedAuth = JSON.parse(localStorage.getItem('auth'));
        if (storedAuth) {
            setAuthState(storedAuth);

            // Set Axios default Authorization header
            axios.defaults.headers.common['Authorization'] = `Bearer ${storedAuth.token}`;
        }

        // Add Axios interceptor to automatically attach token to requests
        const interceptor = axios.interceptors.request.use((config) => {
            const token = localStorage.getItem('auth') 
                ? JSON.parse(localStorage.getItem('auth')).token 
                : null;

            if (token) {
                config.headers.Authorization = `Bearer ${token}`;
            }
            return config;
        });

        // Clean up interceptor on unmount
        return () => axios.interceptors.request.eject(interceptor);
    }, []);

    // Updated setAuth to store token, vendorId, and vendorName
    const setAuth = (authData) => {
        localStorage.setItem('auth', JSON.stringify(authData)); // Store in localStorage
        setAuthState(authData); // Update state

        // Set Axios default Authorization header
        axios.defaults.headers.common['Authorization'] = `Bearer ${authData.token}`;
    };

    const logout = (navigate) => {
        localStorage.removeItem('auth'); // Remove auth data from localStorage
        setAuthState({ token: null, vendorId: null, vendorName: null }); // Clear auth state

        // Remove Axios Authorization header
        delete axios.defaults.headers.common['Authorization'];

        navigate('/'); // Redirect to homepage
    };

    return (
        <AuthContext.Provider value={{ auth, setAuth, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

// Custom hook to use AuthContext
export const useAuth = () => {
    const context = React.useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
