// src/components/NavBar.js
import React, { useContext, useState } from 'react';
import { FaShoppingCart } from 'react-icons/fa';
import { CartContext } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import { Offcanvas, Button } from 'react-bootstrap';
import logo from '../assets/images/logo.png';

const Navbar = () => {
    const { getItemCount, cartItems } = useContext(CartContext);  // Access cart context
    const navigate = useNavigate();
    const [showCart, setShowCart] = useState(false);

    // Toggle cart visibility
    const handleCartClose = () => setShowCart(false);
    const handleCartShow = () => setShowCart(true);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm">
            <div className="container-fluid">
                {/* Logo - Navigate to home on click */}
                <a className="navbar-brand" href="#" onClick={() => navigate('/')}>
                    <img src={logo} alt="Logo" width="180px" className="d-inline-block align-text-top" />
                </a>

                <div className="d-flex align-items-center">
                    {/* Shopping Cart Icon with item count */}
                    <div className="nav-item me-3">
                        <a href="#" className="nav-link position-relative" onClick={handleCartShow} role="button">
                            <FaShoppingCart size={20} />
                            {getItemCount() > 0 && (
                                <span className="badge bg-danger position-absolute top-0 start-100 translate-middle" style={{ fontSize: '0.8rem' }}>
                                    {getItemCount()}
                                </span>
                            )}
                        </a>
                    </div>

                    {/* Sign In Button */}
                    <Button variant="outline-primary" className="rounded-pill" onClick={() => navigate('/sign-in')}>
                        Sign In
                    </Button>
                </div>
            </div>

            {/* Offcanvas Cart */}
            <Offcanvas show={showCart} onHide={handleCartClose} placement="end">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Your Cart</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    {/* Map over cart items */}
                    {cartItems.length > 0 ? (
                        cartItems.map((item, index) => (
                            <div key={index} className="d-flex justify-content-between mb-2">
                                <span>{item.name}</span>
                                <span>{item.quantity} x ${item.price}</span>
                            </div>
                        ))
                    ) : (
                        <p>Your cart is empty.</p>
                    )}
                    {/* Checkout Button */}
                    <Button variant="primary" className="w-100 mt-3" onClick={() => navigate('/checkout')}>
                        Checkout
                    </Button>
                </Offcanvas.Body>
            </Offcanvas>
        </nav>
    );
};

export default Navbar;
