import React, { useState } from 'react';
import { Card, Row, Col, Image, Badge, Button, Modal } from 'react-bootstrap';
import VendorStatus from './VendorStatus';
import MenuPickupDeliveryToggle from './MenuPickupDeliveryToggle';
import './VendorDetails.css';

const VendorDetails = ({ vendor }) => {
  const [showPromoModal, setShowPromoModal] = useState(false);
  const [orderType, setOrderType] = useState('');

  if (!vendor || !vendor.businessHours) {
    return <p>Loading vendor details...</p>;
  }

  const getVendorStatus = () => {
    const currentDay = new Date().toLocaleString('en-US', { weekday: 'long' }).toLowerCase();
    const currentTime = new Date();
    const openTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.open || '00:00'}`
    );
    const closeTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.close || '23:59'}`
    );

    if (currentTime >= openTime && currentTime <= closeTime) {
      return { status: 'open', until: vendor.businessHours[currentDay]?.close };
    }

    const days = Object.keys(vendor.businessHours);
    const nextDayIndex = (days.indexOf(currentDay) + 1) % days.length;
    const nextDay = days[nextDayIndex];
    return { status: 'closed', until: vendor.businessHours[nextDay]?.open };
  };

  const vendorStatus = getVendorStatus();

  return (
    <Card
      className="p-3 shadow-sm"
      style={{
        backgroundColor: '#F5F5F5',
        borderRadius: '20px 20px 0 0', /* Rounded top corners */
        border: 'none',
        marginTop: '0rem',
        zIndex: '1200',
        borderBottom: 'none',
      }}
    >
      {/* Top Row: Vendor Logo, Promo Button, and Toggle */}
      <Row className="align-items-center">
        {/* Vendor Logo */}
        <Col xs="auto" className="d-flex align-items-center">
          <Image
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={`${vendor.dispensaryName} logo`}
            className="rounded"
            style={{ width: '40px', height: '40px', marginRight: '15px' }}
          />
        </Col>

        {/* Promo Button */}
        {vendor.dailyPromo?.title && (
          <Col xs="auto" className="d-flex align-items-center">
            <Button
              variant="outline-dark"
              onClick={() => setShowPromoModal(true)}
              className="d-flex align-items-center justify-content-center promo-button"
              style={{
                borderRadius: '20px',
                fontSize: '0.75rem',
                padding: '0.25rem 0.5rem',
                height: '24px',
              }}
            >
              <i className="bi bi-tag-fill me-1"></i>
              {vendor.dailyPromo.title.slice(0, 10)}...
            </Button>
          </Col>
        )}

        {/* Toggle Button */}
        <Col className="d-flex justify-content-end">
          <MenuPickupDeliveryToggle
            vendor={vendor}
            orderType={orderType}
            setOrderType={setOrderType}
          />
        </Col>
      </Row>

      {/* Vendor Details */}
      <Row>
        <Col>
          <p className="font-medium mt-1 mb-0">{vendor.dispensaryName}</p>

          {/* Vendor Status */}
          <div className="d-flex align-items-center" style={{marginTop: '1px', fontSize: '13px', cursor: "pointer"}}>
            <VendorStatus vendor={vendor} vendorStatus={vendorStatus} />
          </div>

          {/* Payment Method Badges */}
          <div className="mb-2 mt-1">
            {vendor.acceptedPayments.map((payment, index) => (
              <Badge
                key={index}
                bg="light"
                className="me-2 text-dark border border-dark"
                style={{
                  borderRadius: '5px',
                  fontSize: '0.7rem',
                  padding: '0.1rem 0.4rem',
                  height: '20px',
                  lineHeight: '1.2', // Adjust line height for slimmer look
                  fontWeight: 'normal'
                }}
              >
                {payment.method}
              </Badge>
            ))}
          </div>
        </Col>
      </Row>

      {/* Promo Modal */}
      <Modal show={showPromoModal} onHide={() => setShowPromoModal(false)}>
  <Modal.Header closeButton>
    <Modal.Title>{vendor.dailyPromo?.title}</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    {/* Promo Description */}
    <p>{vendor.dailyPromo?.description || 'No description provided.'}</p>

    {/* Promo Image */}
    {vendor.dailyPromo?.promoUrl && (
      <div className="text-center my-3">
        <img
          src={vendor.dailyPromo.promoUrl}
          alt="Promo"
          style={{ maxWidth: '80%', height: 'auto', borderRadius: '8px' }}
        />
        <p className="text-muted mt-2">Present coupon in-person checkout to apply.</p>
      </div>
    )}

    {/* Applicable to Sale Items Note */}
    {vendor.dailyPromo?.applicableToSaleItems !== undefined && (
      <p
        style={{
          fontWeight: 'bold',
          color: vendor.dailyPromo.applicableToSaleItems ? 'green' : 'red',
        }}
      >
        {vendor.dailyPromo.applicableToSaleItems
          ? 'Applicable to Sale Items!'
          : 'Not applicable to sale items.'}
      </p>
    )}
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary" onClick={() => setShowPromoModal(false)}>
      Close
    </Button>
  </Modal.Footer>
</Modal>

    </Card>
  );
};

export default VendorDetails;
