import React from 'react';

const MenuPickupDeliveryToggle = ({ referrer, vendor, orderType, setOrderType }) => {
  const isDeliveryAvailable =
    vendor.dispensaryType === 'Delivery' || vendor.dispensaryType === 'Pickup & Delivery';
  const isPickupAvailable =
    vendor.dispensaryType === 'Pickup' || vendor.dispensaryType === 'Pickup & Delivery';

  // Determine initial toggle state based on referrer
  React.useEffect(() => {
    if (referrer === 'NearbyDelivery' && isDeliveryAvailable) {
      setOrderType('Delivery');
    } else if (referrer === 'NearbyPickup' && isPickupAvailable) {
      setOrderType('Pickup');
    } else if (isPickupAvailable) {
      setOrderType('Pickup');
    } else if (isDeliveryAvailable) {
      setOrderType('Delivery');
    }
  }, [referrer, isDeliveryAvailable, isPickupAvailable, setOrderType]);

  return (
    <div className="btn-group" role="group" aria-label="Delivery or Pickup toggle">
      {/* Delivery Button */}
      <input
        type="radio"
        className="btn-check"
        name="btnradio"
        id="btnradio1"
        autoComplete="off"
        checked={orderType === 'Delivery'}
        disabled={!isDeliveryAvailable}
        onChange={() => isDeliveryAvailable && setOrderType('Delivery')}
      />
      <label
        className={`btn ${orderType === 'Delivery' ? 'text-white' : 'text-secondary'}`}
        style={{
          backgroundColor: orderType === 'Delivery' ? '#1E2024' : 'white',
          border: '1px solid black',
          padding: '4px 12px',
          fontSize: '14px',
          fontFamily: 'Poppins, sans-serif',
          fontWeight: orderType === 'Delivery' ? '600' : '400',
        }}
        htmlFor="btnradio1"
      >
        Delivery
        {!isDeliveryAvailable && <div style={{ fontSize: '10px' }}>(Unavailable)</div>}
      </label>

      {/* Pickup Button */}
      <input
        type="radio"
        className="btn-check"
        name="btnradio"
        id="btnradio2"
        autoComplete="off"
        checked={orderType === 'Pickup'}
        disabled={!isPickupAvailable}
        onChange={() => isPickupAvailable && setOrderType('Pickup')}
      />
      <label
        className={`btn ${orderType === 'Pickup' ? 'text-white' : 'text-secondary'}`}
        style={{
          backgroundColor: orderType === 'Pickup' ? '#1E2024' : 'white',
          border: '1px solid black',
          padding: '4px 12px',
          fontSize: '14px',
          fontFamily: 'Poppins, sans-serif',
          fontWeight: orderType === 'Pickup' ? '600' : '400',
        }}
        htmlFor="btnradio2"
      >
        Pickup
        {!isPickupAvailable && <div style={{ fontSize: '10px' }}>(Unavailable)</div>}
      </label>
    </div>
  );
};

export default MenuPickupDeliveryToggle;
