import React, { useEffect, useState } from 'react';
import ChangeAddressLink from './ChangeAddressLink';
import VendorCard from './VendorCard';

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [address] = useState(localStorage.getItem('userAddress') || ''); // Removed setAddress
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchVendors = async () => {
      if (!address) {
        setError('Please provide a valid address.');
        return;
      }

      setLoading(true); // Start loading
      try {
        const geocodeResponse = await fetch(
          `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
            address
          )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
        );

        const geocodeData = await geocodeResponse.json();

        if (geocodeData.status !== 'OK') {
          setError('Could not fetch location for the given address.');
          setLoading(false); // Stop loading
          return;
        }

        const location = geocodeData.results[0].geometry.location;
        const { lat, lng } = location;

        const vendorsResponse = await fetch(`http://localhost:5001/api/vendors/nearby?lat=${lat}&lng=${lng}`);
        const vendorsData = await vendorsResponse.json();

        if (!vendorsData.vendors) {
          setError('No vendors found near your location.');
          setLoading(false); // Stop loading
          return;
        }

        setVendors(vendorsData.vendors);
      } catch (err) {
        console.error('Error fetching vendors:', err);
        setError('Failed to load vendors. Please try again later.');
      } finally {
        setLoading(false); // Stop loading
      }
    };

    fetchVendors();
  }, [address]);

  if (!address) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container mt-4">
      <h1>Vendors Near You</h1>
      <ChangeAddressLink />
      {loading ? (
        <p>Loading vendors...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : vendors.length ? (
        <div className="vendor-list">
          {vendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      ) : (
        <p>No vendors found near your address.</p>
      )}
    </div>
  );
};

export default NearbyVendorsPage;
