import React from 'react';
import { Card, Row, Col, Image } from 'react-bootstrap';
import VendorStatusDropdown from './VendorStatusDropdown';

const VendorDetails = ({ vendor }) => {
  return (
    <Card className="vendor-details mb-4 p-3">
      <Row className="align-items-center">
        {/* Vendor Logo */}
        <Col xs={3} md={2}>
          <Image
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={`${vendor.dispensaryName} logo`}
            roundedCircle
            style={{
              width: '80px',
              height: '80px',
              objectFit: 'cover',
              border: '1px solid #ddd',
            }}
          />
        </Col>

        {/* Vendor Name and Business Hours */}
        <Col xs={9} md={10}>
          <h5 style={{ fontWeight: 'bold' }}>{vendor.dispensaryName}</h5>
          <div>
            <VendorStatusDropdown />
            <strong>Business Hours:</strong>
            <ul className="list-unstyled mb-0">
              {Object.entries(vendor.businessHours).map(([day, hours]) => (
                <li key={day}>
                  {day.charAt(0).toUpperCase() + day.slice(1)}: {hours.open} - {hours.close}
                </li>
              ))}
            </ul>
          </div>
        </Col>
      </Row>
    </Card>
  );
};

export default VendorDetails;
