// File: src/components/AuthTestButton.js

import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const AuthTestButton = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleCheckCustomer = async () => {
    setLoading(true);

    // Hardcoded phone number for testing
    const phone = '5555551014';

    try {
      // Make API call to backend
      const response = await fetch(`http://localhost:5001/api/customer/check?phone=${phone}`, {
        method: 'GET',
      });

      const statusCode = response.status;
      const data = await response.json();

      // Check and display response
      if (statusCode === 200 && data.customer) {
        setResult(`Customer Data:\n${JSON.stringify(data.customer, null, 2)}`);
        alert(`Customer Data:\n${JSON.stringify(data.customer, null, 2)}`);
      } else {
        setResult(`Customer not found or error occurred.`);
        alert(`Error:\n${data.message || 'Unknown error'}`);
      }
    } catch (error) {
      // Handle and display errors
      setResult(`Error occurred:\n${error.message}`);
      alert(`Error occurred:\n${error.message}`);
      console.error('Error details:', error);
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="d-flex flex-column align-items-center mt-5">
      <Button
        variant="primary"
        onClick={handleCheckCustomer}
        disabled={loading}
        className="test-auth-button"
      >
        {loading ? 'Checking...' : 'Check Customer by Phone'}
      </Button>
      {result && (
        <div className="mt-3 text-center">
          <strong>Result:</strong>
          <pre style={{ textAlign: 'left' }}>{result}</pre>
        </div>
      )}
    </div>
  );
};

export default AuthTestButton;
