import React, { useState, useEffect } from 'react';
import PickupDeliveryToggle from './PickupDeliveryToggle';
import AddressFilter from './AddressFilter';
import VendorList from './VendorList';
import vendors from '../data/vendors';
import AdCarousel from './AdCarousel';
import AddressInput
 from './AddressInput';
import './VendorPage.scss';

const VendorPage = () => {
  const [serviceType, setServiceType] = useState('Delivery'); // Default mode is 'Delivery'
  const [selectedCity, setSelectedCity] = useState('Los Angeles'); // Default city
  const [filteredVendors, setFilteredVendors] = useState([]); // Stores the filtered vendors

  // Function to handle address change (extracting the city from the address)
  const handleAddressChange = (address) => {
    const city = address.split(',').pop().trim(); // Extract city from the address
    setSelectedCity(city); // Update selected city
  };

  // Function to handle toggle between Pickup and Delivery
  const handleToggle = (newMode) => {
    setServiceType(newMode); // Update service type (Pickup or Delivery)
  };

  // Filter vendors based on service type and city
  useEffect(() => {
    const filtered = vendors.filter(
      (vendor) => vendor.services.includes(serviceType) && vendor.city === selectedCity
    );
    setFilteredVendors(filtered); // Update the filtered vendors
  }, [serviceType, selectedCity]); // Re-run the filter whenever serviceType or selectedCity changes

  return (
    <div>
      <div className='my-3 ms-5 me-5'>
      <AdCarousel />
      </div>

      <div className='bg-light custom-shadow-top'>
        <PickupDeliveryToggle onToggle={handleToggle} />
        <AddressFilter onAddressChange={handleAddressChange} />
        <VendorList vendors={filteredVendors} />
      </div>

    </div>
  );
};

export default VendorPage;
