import React from 'react';
import { useNavigate } from 'react-router-dom';
import ProductCard from './ProductCard';

const VendorCard = ({ vendor }) => {
  const navigate = useNavigate();

  const handleViewMenu = () => {
    navigate(`/vendor/${vendor._id}`);
  };

  return (
    <div className="vendor-card shadow p-3 mb-4 rounded" style={{ border: '1px solid #ddd' }}>
      {/* Vendor Header */}
      <div className="d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center">
          <img
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={vendor.dispensaryName}
            className="rounded"
            style={{
              width: '44px',
              height: '44px',
              objectFit: 'cover',
              marginRight: '10px',
            }}
          />
          <div>
            <h6 className="mb-1" style={{ fontSize: '1rem', fontWeight: 'bold' }}>
              {vendor.dispensaryName}
            </h6>
            <p className="text-muted mb-0" style={{ fontSize: '0.85rem' }}>
              Minimum Order: ${vendor.minOrder || 'N/A'} | {vendor.milesAway || 'N/A'} miles away
            </p>
          </div>
        </div>
        <button className="btn btn-primary btn-sm" onClick={handleViewMenu}>
          Menu
        </button>
      </div>

      {/* Products Row */}
      <div className="products-preview mt-3">
        <div className="d-flex overflow-auto" style={{ paddingBottom: '10px', scrollbarWidth: 'thin' }}>
          {vendor.products?.length ? (
            vendor.products.slice(0, 10).map((product) => (
              <div key={product._id} className="me-2" style={{ minWidth: '200px', flex: '0 0 auto' }}>
                <ProductCard product={product} />
              </div>
            ))
          ) : (
            <p className="text-muted">No products available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default VendorCard;
