import React, { useState, useContext } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const ProductDetails = ({ show, handleClose, product, vendorName, logoUrl, vendorId }) => {
  const { addToCart, cartItems } = useContext(CartContext);
  const [localQuantity, setLocalQuantity] = useState(1);

  if (!product) return null;

  // Safeguard values to avoid undefined errors
  const {
    name = 'N/A',
    brand = 'N/A',
    description = '',
    category = 'Uncategorized',
    strain = 'N/A',
    thcContent = null,
    price = 0,
    salePrice = null,
    image = '/placeholder-image.png',
    amount = 'N/A',
  } = product;

  // Find the current quantity of the product in the cart
  const cartItem = cartItems.find((item) => item._id === product._id);
  const currentQuantity = cartItem ? cartItem.quantity : 0;

  // Add to Cart Handler
  const handleAddToCart = () => {
    addToCart({
      ...product,
      quantity: localQuantity,
      price: salePrice || price, // Use salePrice if available
      vendorName,
      logoUrl,
      primaryImage: image, // Ensure image is passed
      vendorId, // Include vendorId explicitly
    });
  };


  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>
          <strong>{brand}</strong> | {category}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="text-center">
          <img
            src={image}
            alt={name}
            className="img-fluid mb-4"
            style={{ maxHeight: '200px', objectFit: 'contain' }}
          />
        </div>
        <div className="d-flex align-items-center mb-3">
          <h5 className="fw-bold mb-0 me-3">{name}</h5>
          {amount && (
            <span
              className="badge bg-light text-dark"
              style={{
                fontSize: '0.9rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.3rem 0.6rem',
              }}
            >
              {amount}
            </span>
          )}
        </div>
        <p className="text-muted">{description}</p>

        <div className="mb-2 d-flex align-items-center">
          <strong>{strain}</strong>
          {thcContent && (
            <span
              className="badge border border-secondary text-secondary ms-3"
              style={{
                fontSize: '0.9rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.3rem 0.6rem',
              }}
            >
              {thcContent}% THC
            </span>
          )}
        </div>
      </Modal.Body>

      {/* Footer */}
      <Modal.Footer className="d-flex justify-content-between align-items-center">
        <div>
          {salePrice ? (
            <>
              <span
                className="badge bg-light"
                style={{
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  color: '#FF0080',
                  borderRadius: '0.5rem',
                  padding: '0.6rem 1.2rem',
                }}
              >
                ${salePrice.toFixed(2)}
              </span>
              <span
                style={{
                  textDecoration: 'line-through',
                  color: 'gray',
                  fontSize: '1rem',
                  marginLeft: '0.5rem',
                }}
              >
                ${price.toFixed(2)}
              </span>
            </>
          ) : (
            <span
              className="badge bg-light text-dark"
              style={{
                fontSize: '1.2rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.6rem 1.2rem',
              }}
            >
              ${price.toFixed(2)}
            </span>
          )}
        </div>

        <Button
          variant="primary"
          className="rounded-circle d-flex align-items-center justify-content-center"
          style={{
            width: '3rem',
            height: '3rem',
            fontSize: '1.5rem',
            fontWeight: 'bold',
          }}
          onClick={handleAddToCart}
        >
          +
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ProductDetails;
