import React, { useState, useEffect } from 'react';

const ProductList = () => {
    const [token, setToken] = useState('');
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    // Fetch access token
    const fetchAccessToken = async () => {
        const options = {
            method: 'POST',
            headers: {
                accept: 'application/json',
                'content-type': 'application/x-www-form-urlencoded'
            },
            body: new URLSearchParams({
                client_id: 'wNzewkHXh2P5GnfCFPX3AmeA',
                apikey: 'MTQ5Mjg2ODdjMDBmNTliZDUxZ'
            })
        };

        try {
            const response = await fetch('https://api.treez.io/v2.0/dispensary/partnersandbox2/config/api/gettokens', options);
            const data = await response.json();
            if (data.resultCode === 'SUCCESS') {
                setToken(data.access_token);
                console.log("Access Token Retrieved:", data.access_token);
            } else {
                throw new Error('Failed to retrieve access token');
            }
        } catch (err) {
            setError(`Error fetching token: ${err.message}`);
        }
    };

    // Fetch products with the access token
    const fetchProducts = async () => {
        const options = {
            method: 'GET',
            headers: {
                authorization: token, // Directly use token as authorization header without Bearer
                client_id: 'wNzewkHXh2P5GnfCFPX3AmeA'
            }
        };

        try {
            const response = await fetch('https://api.treez.io/v2.0/dispensary/partnersandbox2/product/product_list?page=1', options);
            const data = await response.json();
            setProducts(data.data || []);
            console.log("Fetched Products:", data);
        } catch (err) {
            setError(`Error fetching products: ${err.message}`);
        }
    };

    // Fetch token on component mount
    useEffect(() => {
        fetchAccessToken();
    }, []);

    // Fetch products when token is available
    useEffect(() => {
        if (token) {
            fetchProducts();
        }
    }, [token]);

    return (
        <div className="container my-4">
            <h2>Simple Product List</h2>
            {error && <p className="text-danger">{error}</p>}
            <div className="row">
                {products.length > 0 ? (
                    products.map((product) => (
                        <div className="col-md-4 mb-3" key={product.product_id}>
                            <div className="card">
                                <img
                                    src={product.e_commerce?.primary_image || 'https://via.placeholder.com/150'}
                                    alt={product.product_configurable_fields.name}
                                    className="card-img-top"
                                />
                                <div className="card-body">
                                    <h5 className="card-title">{product.product_configurable_fields.name}</h5>
                                    <p className="card-text">Price: ${product.pricing.price_sell}</p>
                                    <p className="card-text">Category: {product.category_type}</p>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No products available</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
