import React, { useEffect, useState, useRef } from 'react';
import ChangeAddressLink from './ChangeAddressLink';
import VendorCard from './VendorCard';
import { Form, InputGroup } from 'react-bootstrap'; // Import Bootstrap Form for the search bar
import { GeoAltFill } from 'react-bootstrap-icons'; // Import Bootstrap Icons

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState(''); // State for search query

  // Ref to track API call
  const hasFetched = useRef(false);

  // Base API URL from environment variables
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  // Function to parse the address
  const parseAddress = (address) => {
    const parts = address.split(',');
    return {
      street: parts[0]?.trim() || '',
      city: parts[1]?.trim() || '',
      stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
    };
  };

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parsed = parseAddress(savedAddress);
      setParsedAddress(parsed);
    }
  }, []);

  const fetchVendors = async () => {
    const address = localStorage.getItem('userAddress');
    if (!address) {
      setError('Please provide a valid address.');
      return;
    }

    if (hasFetched.current) {
      console.log('Skipping redundant API call');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      hasFetched.current = true; // Mark as fetched
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          address
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );

      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location for the given address.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || vendorsData.vendors.length === 0) {
        throw new Error('No vendors found near your location.');
      }

      console.log('Fetched vendors:', vendorsData.vendors);
      setVendors(vendorsData.vendors);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch vendors on component mount
  useEffect(() => {
    fetchVendors();
  }, []); // Run only once on mount

  // Filter vendors or products by search query
  const filteredVendors = vendors.filter((vendor) =>
    vendor.dispensaryName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    vendor.products?.some((product) =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.brand?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.strain?.toLowerCase().includes(searchQuery.toLowerCase()) // Filter by strain
    )
  );

  // Component Rendering
  if (!parsedAddress.street && !parsedAddress.city && !parsedAddress.stateAbbreviation) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container-fluid">
      <div className="bg-light py-3 px-4 mb-3 border rounded">
  <div className="d-flex align-items-center justify-content-between">
    {/* Search Bar */}
    <InputGroup className="w-50">
    <Form.Control
      type="text"
      placeholder="Search strains, brands, products, vendors..."
      className="form-control"
      value={searchQuery}
      onChange={(e) => setSearchQuery(e.target.value)}
      style={{ paddingLeft: '2.5rem' }} // Extra padding for the search icon
    />
    {/* Search Icon */}
    <span
      className="position-absolute top-50 start-0 translate-middle-y ps-2 text-secondary"
      style={{ fontSize: '1.2rem' }}
    >
      <i className="bi bi-search"></i> {/* Bootstrap Search Icon */}
    </span>
    </InputGroup>
    <div className="d-flex align-items-center">
      {/* Refresh Button */}
      <Button
        variant="outline-primary"
        className="rounded-circle me-3"
        onClick={handleRefresh}
        style={{
          width: '40px',
          height: '40px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <i className="bi bi-arrow-clockwise"></i> {/* Bootstrap Refresh Icon */}
      </Button>
      {/* Add Product Button */}
      <Button variant="success" onClick={() => setShowAddProduct(true)}>
        + Add Product
      </Button>
    </div>
  </div>
</div>
      {/* Toolbar Section */}
<div className="d-flex align-items-center justify-content-between bg-light p-3 shadow-sm" style={{ margin: 0 }}>
  {/* Address Section */}
  <div className="d-flex align-items-center">
    <GeoAltFill className="text-primary me-2" size={20} />
    <span className="me-2 text-dark fw-light">
      {parsedAddress.street || `${parsedAddress.city}, ${parsedAddress.stateAbbreviation}`}
    </span>
    <ChangeAddressLink />
  </div>

  {/* Search Bar */}
  <div className="position-relative w-50">

  </div>
</div>


      {/* Vendor Cards Section */}
      {loading ? (
        <p>Loading vendors...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : filteredVendors.length ? (
        <div className="vendor-list">
          {filteredVendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      ) : (
        <p>No vendors or products match your search.</p>
      )}
    </div>
  );
};

export default NearbyVendorsPage;
