import React, { useEffect, useState } from 'react';
import VendorCard from './VendorCard';
import PickupMap from './PickupMap';
import './NearbyPickupPage.css'; // Import CSS for animations

const NearbyPickupPage = ({ searchQuery }) => { // Accept searchQuery as a prop
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [cardPosition, setCardPosition] = useState('lower'); // 'lower' or 'middle'
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const fetchVendors = async () => {
    const address = localStorage.getItem('userAddress');
    if (!address) {
      setError('Please provide a valid address.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          address
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );
      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location for the given address.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || vendorsData.vendors.length === 0) {
        throw new Error('No vendors found near your location.');
      }

      setVendors(vendorsData.vendors);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVendors();
  }, []);

  // Filtering logic
  const filteredVendors = vendors.filter((vendor) => {
    return vendor.products?.some((product) => {
      const matchesName = product.name?.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesBrand = product.brand?.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesStrain = product.strain?.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesName || matchesBrand || matchesStrain;
    });
  });

  return (
    <div className="container-fluid p-0">
      {/* Map and Vendor Cards Section */}
      <div>
        {/* Map Section */}
        <div
          style={{
            height: cardPosition === 'middle' ? '0px' : '400px', // Map disappears when card is "middle"
            transition: 'height 0.3s ease-in-out',
            overflow: 'hidden',
          }}
        >
          <PickupMap dispensaries={vendors} onMarkerClick={(vendor) => setSelectedVendor(vendor)} />
        </div>

        {/* Vendor Card Section */}
        <div
          className="vendor-card-container"
          style={{
            position: 'relative', // Adjust relative to page
            bottom: 0,
            width: '100%',
            backgroundColor: '#fff',
            borderTop: '1px solid #ddd',
          }}
        >
          <div
            className="drag-handle"
            onClick={() => setCardPosition(cardPosition === 'middle' ? 'lower' : 'middle')}
            style={{
              cursor: 'pointer',
              height: '5px',
              backgroundColor: '#ccc',
              margin: '10px auto',
              borderRadius: '5px',
              width: '50px',
            }}
          ></div>
          {filteredVendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default NearbyPickupPage;
