import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';

const categories = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Misc'];

const CategoryFilter = ({ selectedCategory, onCategoryChange }) => {
  return (
    <div
      className="category-filter mb-3"
      style={{

        overflowX: 'auto', // Enable horizontal scrolling for overflow
        whiteSpace: 'nowrap', // Prevent wrapping
        padding: '0.5rem', // Add padding for better spacing
      }}
    >
      <ButtonGroup
        className="d-inline-flex" // Inline-flex to allow scrolling
        style={{
          gap: '0.75rem', // Add space between buttons
        }}
      >
        {categories.map((category) => (
          <Button
            key={category}
            variant={selectedCategory === category ? 'dark' : 'light'}
            onClick={() => onCategoryChange(category)}
            style={{
              width: '150px', // Fixed width for each button
              textAlign: 'center', // Center-align text inside buttons
              borderRadius: '20px', // Rounded corners
              padding: '0.5rem 1rem', // Padding for better click area
              border: selectedCategory === category ? '2px solid #333' : '1px solid #ddd',
              fontWeight: 'bold',
              textTransform: 'uppercase',
              transition: 'all 0.3s ease',
            }}
          >
            {category}
          </Button>
        ))}
      </ButtonGroup>
    </div>
  );
};

export default CategoryFilter;
