import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

// Category Mapping
const categoryMapping = {
    'FLOWER': 'Flower',
    'Sativa Flower': 'Flower',
    'Indica Flower': 'Flower',
    'Pre-roll': 'Pre-rolls',
    'Single Pre-roll': 'Pre-rolls',
    'CARTRIDGE': 'Vape',
    'Vape Cartridge': 'Vape',
    'EDIBLE': 'Edible',
    'PILL': 'Edible',
    'BEVERAGE': 'Edible',
    'TINCTURE': 'Concentrate',
    'EXTRACT': 'Concentrate',
    'CONCENTRATE': 'Concentrate',
    'default': 'Misc'  // Unmatched categories fall under "Misc"
};

// Fixed category order
const customCategoryOrder = ['Flower', 'Pre-rolls', 'Vape', 'Edible', 'Concentrate', 'Misc'];

const TestMenu = () => {
    const [productsByCategory, setProductsByCategory] = useState({});

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                organizeProductsByCategory(response.data.product_list || []);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchProducts();
    }, []);

    // Organize products into categories based on mapping
    const organizeProductsByCategory = (products) => {
        const categorizedProducts = products.reduce((acc, product) => {
            const mappedCategory = categoryMapping[product.category_type] || categoryMapping.default;
            if (!acc[mappedCategory]) acc[mappedCategory] = [];
            acc[mappedCategory].push(product);
            return acc;
        }, {});

        setProductsByCategory(categorizedProducts);
    };

    return (
        <Container>
            <h2 className="mb-4">Vendor Menu</h2>

            {/* Display Products by Category in Rows */}
            {customCategoryOrder.map((category) => (
                <div key={category} className="my-4">
                    {productsByCategory[category] && productsByCategory[category].length > 0 && (
                        <>
                            <h4>{category}</h4>
                            <Row>
                                {productsByCategory[category].map((product) => (
                                    <Col xs={12} md={6} lg={4} key={product.product_id} className="mb-4">
                                        <Card>
                                            <Card.Img
                                                variant="top"
                                                src={product.e_commerce?.primary_image || '/placeholder.jpg'}
                                                alt={product.product_configurable_fields.name}
                                            />
                                            <Card.Body>
                                                <Card.Title>{product.product_configurable_fields.name}</Card.Title>
                                                <Card.Text>{product.product_configurable_fields.brand}</Card.Text>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                ))}
                            </Row>
                        </>
                    )}
                </div>
            ))}
        </Container>
    );
};

export default TestMenu;
