// src/components/VendorStorePage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';
import StoreDetails from './StoreDetails';
import TestMenu from './TestMenu';

const VendorStorePage = () => {
    const { id } = useParams();
    const [vendorDetails, setVendorDetails] = useState({});


    useEffect(() => {
        const fetchVendorData = async () => {
            try {
                // Fetch vendor details
                const detailsResponse = await api.get(`/vendors/${id}`);
                setVendorDetails(detailsResponse.data.data);

                // Fetch vendor menu using the updated route
                const menuResponse = await api.get(`/vendors/${id}/menu`);
                setVendorMenu(menuResponse.data.data); // Set the menu data
            } catch (error) {
                console.error("Error fetching vendor data:", error);
            }
        };

        fetchVendorData();
    }, [id]);

    return (
        <div>
            {/* Vendor Details Component */}
            <StoreDetails vendor={vendorDetails} />

            {/* Vendor Menu Section */}
            <div>
                <h3>Menu</h3>
            </div>
        </div>
    );
};

export default VendorStorePage;
