import React, { useState, useContext } from 'react';
import { Card, Row, Col, Badge, Button, Modal, Image, Dropdown } from 'react-bootstrap';
import { Tag, Phone, MessageCircle, Clock } from 'lucide-react';
import { VendorContext } from '../context/VendorContext';
import MenuPickupDeliveryToggle from './MenuPickupDeliveryToggle';
import './VendorDetails.css';

const VendorDetails = ({ 
  vendor = {}, 
  orderType, 
  setOrderType 
}) => {
  const { vendorStatus } = useContext(VendorContext);
  const [showPromoModal, setShowPromoModal] = useState(false);

  // Destructure vendor with default values
  const {
    logoUrl = '/placeholder-logo.png',
    dispensaryName = '',
    minOrder = 0,
    acceptedPayments = [],
    dailyPromo = null,
    businessHours = {},
    contactNumber: { formatted: contactNumber = '' } = {},
  } = vendor || {};

  // Format time to AM/PM format
  const formatTime = (time) => {
    if (!time) return 'Closed';
    const [hour, minute] = time.split(':').map(Number);
    const formattedHour = hour % 12 || 12; // Convert 24-hour to 12-hour format
    const ampm = hour >= 12 ? 'PM' : 'AM';
    return `${formattedHour}:${minute.toString().padStart(2, '0')} ${ampm}`;
  };

  // Custom dropdown toggle component
  const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <div
      ref={ref}
      onClick={(e) => {
        e.preventDefault();
        onClick(e);
      }}
      className="d-flex align-items-center"
      style={{ cursor: 'pointer' }}
    >
      {vendorStatus && (
        <>
          <div
            className={`rounded-circle me-2 ${
              vendorStatus.status === 'open' ? 'bg-success' : 'bg-danger'
            }`}
            style={{
              width: '10px',
              height: '10px',
            }}
          />
          <span className="text-gray-600" style={{ fontSize: '0.875rem' }}>
            {vendorStatus.status === 'open'
              ? `Open until ${formatTime(vendorStatus.until)}`
              : `Closed until ${formatTime(vendorStatus.until)}`}
          </span>
        </>
      )}
      {children}
    </div>
  ));

  if (!vendor) return null;

  const currentDay = vendorStatus?.currentDay || '';

  return (
    <Card className="border-0 vendor-details">
      <Card.Body className="p-3">
        {/* Top Row: Logo, Name, Status, and Toggle */}
        <Row className="align-items-center mb-2">
          <Col className="d-flex align-items-center">
            <div className="d-flex align-items-center gap-3">
              <Image
                src={logoUrl}
                alt={`${dispensaryName || 'Vendor'} logo`}
                className="rounded-circle vendor-logo"
                style={{ width: '48px', height: '48px', objectFit: 'cover' }}
                onError={(e) => {
                  e.target.src = '/placeholder-logo.png';
                }}
              />
              <div>
                <h2 className="fs-5 fw-semibold mb-1">{dispensaryName}</h2>
                {vendorStatus && businessHours && Object.keys(businessHours).length > 0 && (
                  <Dropdown className="position-relative" style={{ zIndex: 1700 }}>
                    <Dropdown.Toggle as={CustomToggle} id="vendor-status-dropdown" />
                    <Dropdown.Menu align="end" className="shadow-sm border-0 p-3" style={{ minWidth: '280px', zIndex: 1700 }}>
                      {/* Contact Links */}
                      {contactNumber && (
                        <div className="d-flex justify-content-around mb-3">
                          <a href={`tel:${contactNumber}`} className="text-decoration-none text-gray-700 d-flex align-items-center gap-2">
                            <Phone size={16} />
                            <span style={{ fontSize: '0.875rem' }}>Call</span>
                          </a>
                          <a href={`sms:${contactNumber}`} className="text-decoration-none text-gray-700 d-flex align-items-center gap-2">
                            <MessageCircle size={16} />
                            <span style={{ fontSize: '0.875rem' }}>Text</span>
                          </a>
                        </div>
                      )}

                      {/* Minimum Order */}
                      {minOrder > 0 && (
                        <>
                          <hr className="my-3" />
                          <div className="d-flex align-items-center justify-content-center">
                            <span className="text-gray-600" style={{ fontSize: '0.875rem' }}>
                              ${minOrder} minimum order
                            </span>
                          </div>
                        </>
                      )}

                      <hr className="my-3" />

                      {/* Business Hours */}
                      <div className="mb-2 d-flex align-items-center gap-2">
                        <Clock size={16} className="text-gray-600" />
                        <span className="fw-medium" style={{ fontSize: '0.875rem' }}>Business Hours</span>
                      </div>
                      <div className="space-y-1">
                        {Object.entries(businessHours).map(([day, hours]) => (
                          <div key={day} 
                            className={`d-flex justify-content-between align-items-center ${day === currentDay ? 'fw-medium' : ''}`}
                            style={{ 
                              fontSize: '0.875rem',
                              backgroundColor: day === currentDay ? '#f8f9fa' : 'transparent',
                              padding: '0.25rem 0.5rem',
                              borderRadius: '4px'
                            }}>
                            <span className={day === currentDay ? 'text-dark' : 'text-gray-600'}>
                              {day.charAt(0).toUpperCase() + day.slice(1)}
                            </span>
                            <span className={day === currentDay ? 'text-dark' : 'text-gray-800'}>
                              {formatTime(hours.open)} - {formatTime(hours.close)}
                            </span>
                          </div>
                        ))}
                      </div>
                    </Dropdown.Menu>
                  </Dropdown>
                )}
              </div>
            </div>
          </Col>
          <Col xs="auto">
            {setOrderType && (
              <MenuPickupDeliveryToggle
                vendor={vendor}
                orderType={orderType}
                setOrderType={setOrderType}
                className="transform-scale-90"
              />
            )}
          </Col>
        </Row>

        {/* Bottom Row: Payment Methods and Promo Button */}
        <Row className="align-items-center">
          <Col>
            {acceptedPayments.length > 0 && (
              <div className="d-flex flex-wrap gap-2">
                {acceptedPayments.map((payment, index) => (
                  <Badge
                    key={index}
                    bg="white"
                    text="dark"
                    className="border"
                    style={{ 
                      fontSize: '0.75rem', 
                      fontWeight: 'normal',
                      padding: '0.35rem 0.75rem'
                    }}
                  >
                    {payment.method}
                  </Badge>
                ))}
              </div>
            )}
          </Col>
          <Col xs="auto">
            {dailyPromo?.title && (
              <Button
                variant="outline-secondary"
                size="sm"
                onClick={() => setShowPromoModal(true)}
                className="d-flex align-items-center gap-2 rounded-pill"
                style={{ fontSize: '0.75rem', padding: '0.375rem 1rem' }}
              >
                <Tag size={16} />
                <span className="text-truncate" style={{ maxWidth: '150px' }}>
                  {dailyPromo.title}
                </span>
              </Button>
            )}
          </Col>
        </Row>

        {/* Promo Modal */}
        {dailyPromo && (
          <Modal show={showPromoModal} onHide={() => setShowPromoModal(false)} centered>
            <Modal.Header closeButton>
              <Modal.Title className="fs-5">{dailyPromo.title}</Modal.Title>
            </Modal.Header>
            <Modal.Body className="p-4">
              <p className="text-secondary mb-4">{dailyPromo.description || 'No description provided.'}</p>
              {dailyPromo.promoUrl && (
                <div className="text-center">
                  <img
                    src={dailyPromo.promoUrl}
                    alt="Promo"
                    className="img-fluid rounded mb-2"
                    style={{ maxWidth: '90%' }}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                  <p className="text-secondary small mt-2">Present coupon in-person during payment to apply.</p>
                </div>
              )}
              {dailyPromo.applicableToSaleItems !== undefined && (
                <p className={`fw-medium ${dailyPromo.applicableToSaleItems ? 'text-success' : 'text-danger'} mb-0 mt-3`}>
                  {dailyPromo.applicableToSaleItems ? 'Applicable to Sale Items!' : 'Not applicable to sale items.'}
                </p>
              )}
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowPromoModal(false)}>Close</Button>
            </Modal.Footer>
          </Modal>
        )}
      </Card.Body>
    </Card>
  );
};

export default VendorDetails;
