import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const StoreNoticeComponent = ({ initialNotice = '', onUpdate }) => {
  const [storeNotice, setStoreNotice] = useState(initialNotice); // Initialize with initialNotice
  const [charCount, setCharCount] = useState(initialNotice.length);
  const [error, setError] = useState('');

  // Update state when initialNotice changes
  useEffect(() => {
    if (initialNotice !== undefined) {
        setStoreNotice(initialNotice);
        setCharCount(initialNotice.length);
    }
}, [initialNotice]);


  const handleChange = (e) => {
    const text = e.target.value;
    if (text.length <= 150) {
      setStoreNotice(text);
      setCharCount(text.length);
      setError('');
    } else {
      setError('Notice cannot exceed 150 characters.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (storeNotice.length > 150) {
      setError('Notice cannot exceed 150 characters.');
      return;
    }
    onUpdate(storeNotice); // Pass updated notice to parent
  };

  return (
    <Form onSubmit={handleSubmit} className="p-4 border rounded shadow-sm">
      <h5 className="mb-3">Store Notice</h5>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form.Group controlId="storeNotice" className="mb-3">
        <Form.Control
          as="textarea"
          rows={3}
          placeholder="Enter your store notice (max 150 characters)"
          value={storeNotice} // Dynamically bind the value
          onChange={handleChange}
        />
        <Form.Text className="text-muted">
          {charCount} / 150 characters
        </Form.Text>
      </Form.Group>
      <div className="d-flex justify-content-center">
        <Button variant="primary" type="submit" className="mt-2 px-4 py-2">
          Update Notice
        </Button>
      </div>
    </Form>
  );
};

export default StoreNoticeComponent;
